   102â†’// Custom Errors
   103â†’export class TaskNotFoundError extends Error {
   104â†’  constructor(id: string) {
   105â†’    super(`Task with ID ${id} not found`);
   106â†’    this.name = 'TaskNotFoundError';
   107â†’  }
   108â†’}
   109â†’
   110â†’export class TaskHierarchyError extends Error {
   111â†’  constructor(message: string) {
   112â†’    super(message);
   113â†’    this.name = 'TaskHierarchyError';
   114â†’  }
   115â†’}
   116â†’
   117â†’export class DatabaseError extends Error {
   118â†’  constructor(message: string) {
   119â†’    super(`Database error: ${message}`);
   120â†’    this.name = 'DatabaseError';
   121â†’  }
   122â†’}
   123â†’
   124â†’/**
   125â†’ * TaskHierarchyService - SQLite implementation
   126â†’ */
   127â†’export class TaskHierarchyService {
   128â†’  private db: SQLiteDatabase | null = null;
   129â†’  private readonly dbPath: string;
   130â†’
   131â†’  constructor(dbPath: string = './devflow.sqlite') {
   132â†’    this.dbPath = dbPath;
   133â†’  }
   134â†’
   135â†’  /**
   136â†’   * Initialize database connection
   137â†’   */
   138â†’  async initialize(): Promise<void> {
   139â†’    try {
   140â†’      this.db = await open({
   141â†’        filename: this.dbPath,
   142â†’        driver: require('sqlite3').Database
   143â†’      });
   144â†’
   145â†’      // Enable foreign key constraints
   146â†’      await this.db.run('PRAGMA foreign_keys = ON');
   147â†’    } catch (error) {
   148â†’      throw new DatabaseError(`Failed to initialize database: ${error instanceof Error ? error.message : String(error)}`);
   149â†’    }
   150â†’  }
   151â†’
   152â†’  /**
   153â†’   * Close database connection
   154â†’   */
   155â†’  async close(): Promise<void> {
   156â†’    if (this.db) {
   157â†’      await this.db.close();
   158â†’      this.db = null;
   159â†’    }
   160â†’  }
   161â†’
   162â†’  /**
   163â†’   * Create a new task context
   164â†’   */
   165â†’  async createTask(input: CreateTaskContextInput): Promise<TaskContext> {
   166â†’    if (!this.db) {
   167â†’      throw new DatabaseError('Database not initialized');
   168â†’    }
   169â†’
   170â†’    const db = this.db;
   171â†’    const priority = input.priority || TaskPriority.MEDIUM;
   172â†’    const status = input.status || TaskStatus.PLANNING;
   173â†’
   174â†’    try {
   175â†’      // Validate parent exists if specified
   176â†’      if (input.parentTaskId) {
   177â†’        const parentExists = await db.get(
   178â†’          'SELECT 1 FROM task_contexts WHERE id = ?',
   179â†’          input.parentTaskId
   180â†’        );
   181â†’        if (!parentExists) {
   182â†’          throw new TaskHierarchyError(`Parent task ${input.parentTaskId} does not exist`);
   183â†’        }
   184â†’      }
   185â†’
   186â†’      // Insert new task - let SQLite generate ID
   187â†’      const result = await db.run(
   188â†’        `INSERT INTO task_contexts 
   189â†’         (title, description, priority, status, complexity_score, estimated_duration_minutes, 
   190â†’          required_capabilities, primary_platform, platform_routing, architectural_context,
   191â†’          implementation_context, debugging_context, maintenance_context, cc_session_id,
   192â†’          cc_task_file, branch_name, parent_task_id, depends_on)
   193â†’         VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)`,
   194â†’        input.title,
   195â†’        input.description || null,
   196â†’        priority,
   197â†’        status,
   198â†’        input.complexityScore || null,
   199â†’        input.estimatedDurationMinutes || null,
   200â†’        input.requiredCapabilities ? JSON.stringify(input.requiredCapabilities) : null,
   201â†’        input.primaryPlatform || null,
   202â†’        input.platformRouting ? JSON.stringify(input.platformRouting) : null,
   203â†’        JSON.stringify({}), // architectural_context
   204â†’        JSON.stringify({}), // implementation_context
   205â†’        JSON.stringify({}), // debugging_context
   206â†’        JSON.stringify({}), // maintenance_context
   207â†’        input.ccSessionId || null,
   208â†’        input.ccTaskFile || null,
   209â†’        input.branchName || null,
   210â†’        input.parentTaskId || null,
   211â†’        input.dependsOn ? JSON.stringify(input.dependsOn) : null
   212â†’      );
   213â†’
   214â†’      // Get the inserted task using rowid
   215â†’      const task = await db.get(
   216â†’        `SELECT * FROM task_contexts WHERE rowid = ?`,
   217â†’        result.lastID
   218â†’      );
   219â†’
   220â†’      if (!task) {
   221â†’        throw new DatabaseError('Failed to retrieve created task');
   222â†’      }
   223â†’
   224â†’      return this.mapRowToTaskContext(task);
   225â†’    } catch (error) {
   226â†’      if (error instanceof TaskHierarchyError || error instanceof DatabaseError) {
   227â†’        throw error;
   228â†’      }
   229â†’      throw new DatabaseError(`Failed to create task: ${error instanceof Error ? error.message : String(error)}`);
   230â†’    }
   231â†’  }
   232â†’
   233â†’  /**
   234â†’   * Get task by ID
   235â†’   */
   236â†’  async getTaskById(id: string): Promise<TaskContext | null> {
   237â†’    if (!this.db) {
   238â†’      throw new DatabaseError('Database not initialized');
   239â†’    }
   240â†’
   241â†’    try {
   242â†’      const row = await this.db.get(
   243â†’        `SELECT * FROM task_contexts WHERE id = ?`,
   244â†’        id
   245â†’      );
   246â†’
   247â†’      if (!row) {
   248â†’        return null;
   249â†’      }
   250â†’
   251â†’      return this.mapRowToTaskContext(row);
   252â†’    } catch (error) {
   253â†’      throw new DatabaseError(`Failed to get task: ${error instanceof Error ? error.message : String(error)}`);
   254â†’    }
   255â†’  }
   256â†’
   257â†’  /**
   258â†’   * Update task context
   259â†’   */
   260â†’  async updateTask(id: string, input: UpdateTaskContextInput): Promise<TaskContext> {
   261â†’    if (!this.db) {
   262â†’      throw new DatabaseError('Database not initialized');
   263â†’    }
   264â†’
   265â†’    const db = this.db;
   266â†’    const now = new Date().toISOString();
   267â†’
   268â†’    try {
   269â†’      // Check if task exists
   270â†’      const existingTask = await this.getTaskById(id);
   271â†’      if (!existingTask) {
   272â†’        throw new TaskNotFoundError(id);
   273â†’      }
   274â†’
   275â†’      // Build update query dynamically
   276â†’      const updates: string[] = [];
   277â†’      const values: any[] = [];
   278â†’
   279â†’      if (input.title !== undefined) {
   280â†’        updates.push('title = ?');
   281â†’        values.push(input.title);
   282â†’      }
   283â†’
   284â†’      if (input.description !== undefined) {
   285â†’        updates.push('description = ?');
   286â†’        values.push(input.description);
   287â†’      }
   288â†’
   289â†’      if (input.priority !== undefined) {
   290â†’        updates.push('priority = ?');
   291â†’        values.push(input.priority);
   292â†’      }
   293â†’
   294â†’      if (input.status !== undefined) {
   295â†’        updates.push('status = ?');
   296â†’        values.push(input.status);
   297â†’      }
   298â†’
   299â†’      if (input.complexityScore !== undefined) {
   300â†’        updates.push('complexity_score = ?');
   301â†’        values.push(input.complexityScore);
   302â†’      }
   303â†’
   304â†’      if (input.estimatedDurationMinutes !== undefined) {
   305â†’        updates.push('estimated_duration_minutes = ?');
   306â†’        values.push(input.estimatedDurationMinutes);
   307â†’      }
   308â†’
   309â†’      if (input.primaryPlatform !== undefined) {
   310â†’        updates.push('primary_platform = ?');
   311â†’        values.push(input.primaryPlatform);
   312â†’      }
   313â†’
   314â†’      if (input.ccSessionId !== undefined) {
   315â†’        updates.push('cc_session_id = ?');
   316â†’        values.push(input.ccSessionId);
   317â†’      }
   318â†’
   319â†’      if (input.parentTaskId !== undefined) {
   320â†’        updates.push('parent_task_id = ?');
   321â†’        values.push(input.parentTaskId);
   322â†’      }
   323â†’
   324â†’      // Always update the updated_at timestamp
   325â†’      updates.push('updated_at = ?');
   326â†’      values.push(now);
   327â†’
   328â†’      // If no updates, return existing task
   329â†’      if (updates.length === 1) { // Only updated_at would be updated
   330â†’        return existingTask;
   331â†’      }
   332â†’
   333â†’      values.push(id); // For WHERE clause
   334â†’
   335â†’      // Execute update
   336â†’      await db.run(
   337â†’        `UPDATE task_contexts SET ${updates.join(', ')} WHERE id = ?`,
   338â†’        ...values
   339â†’      );
   340â†’
   341â†’      // Return updated task
   342â†’      const updatedTask = await this.getTaskById(id);
   343â†’      if (!updatedTask) {
   344â†’        throw new DatabaseError('Failed to retrieve updated task');
   345â†’      }
   346â†’
   347â†’      return updatedTask;
   348â†’    } catch (error) {
   349â†’      if (error instanceof TaskNotFoundError) {
   350â†’        throw error;
   351â†’      }
   352â†’      throw new DatabaseError(`Failed to update task: ${error instanceof Error ? error.message : String(error)}`);
   353â†’    }
   354â†’  }
   355â†’
   356â†’  /**
   357â†’   * Delete task context
   358â†’   */
   359â†’  async deleteTask(id: string): Promise<void> {
   360â†’    if (!this.db) {
   361â†’      throw new DatabaseError('Database not initialized');
   362â†’    }
   363â†’
   364â†’    const db = this.db;
   365â†’
   366â†’    try {
   367â†’      // Check if task exists
   368â†’      const existingTask = await this.getTaskById(id);
   369â†’      if (!existingTask) {
   370â†’        throw new TaskNotFoundError(id);
   371â†’      }
   372â†’
   373â†’      // Check for child tasks
   374â†’      const childTasks = await db.get(
   375â†’        'SELECT 1 FROM task_contexts WHERE parent_task_id = ? LIMIT 1',
   376â†’        id
   377â†’      );
   378â†’
   379â†’      if (childTasks) {
   380â†’        throw new TaskHierarchyError(`Cannot delete task ${id} because it has child tasks`);
   381â†’      }
   382â†’
   383â†’      // Delete the task
   384â†’      await db.run('DELETE FROM task_contexts WHERE id = ?', id);
   385â†’    } catch (error) {
   386â†’      if (error instanceof TaskNotFoundError || error instanceof TaskHierarchyError) {
   387â†’        throw error;
   388â†’      }
   389â†’      throw new DatabaseError(`Failed to delete task: ${error instanceof Error ? error.message : String(error)}`);
   390â†’    }
   391â†’  }
   392â†’
   393â†’  /**
   394â†’   * Get all child tasks of a parent task
   395â†’   */
   396â†’  async getChildTasks(parentId: string): Promise<TaskContext[]> {
   397â†’    if (!this.db) {
   398â†’      throw new DatabaseError('Database not initialized');
   399â†’    }
   400â†’
   401â†’    try {
   402â†’      // Verify parent exists
   403â†’      const parentExists = await this.getTaskById(parentId);
   404â†’      if (!parentExists) {
   405â†’        throw new TaskNotFoundError(parentId);
   406â†’      }
   407â†’
   408â†’      const rows = await this.db.all(
   409â†’        `SELECT * FROM task_contexts WHERE parent_task_id = ? ORDER BY created_at ASC`,
   410â†’        parentId
   411â†’      );
   412â†’
   413â†’      return rows.map(row => this.mapRowToTaskContext(row));
   414â†’    } catch (error) {
   415â†’      if (error instanceof TaskNotFoundError) {
   416â†’        throw error;
   417â†’      }
   418â†’      throw new DatabaseError(`Failed to get child tasks: ${error instanceof Error ? error.message : String(error)}`);
   419â†’    }
   420â†’  }
   421â†’
   422â†’  /**
   423â†’   * Get root tasks (tasks with no parent)
   424â†’   */
   425â†’  async getRootTasks(): Promise<TaskContext[]> {
   426â†’    if (!this.db) {
   427â†’      throw new DatabaseError('Database not initialized');
   428â†’    }
   429â†’
   430â†’    try {
   431â†’      const rows = await this.db.all(
   432â†’        `SELECT * FROM task_contexts WHERE parent_task_id IS NULL ORDER BY created_at ASC`
   433â†’      );
   434â†’
   435â†’      return rows.map(row => this.mapRowToTaskContext(row));
   436â†’    } catch (error) {
   437â†’      throw new DatabaseError(`Failed to get root tasks: ${error instanceof Error ? error.message : String(error)}`);
   438â†’    }
   439â†’  }
   440â†’
   441â†’  /**
   442â†’   * Get full task hierarchy starting from a root task
   443â†’   */
   444â†’  async getTaskHierarchy(rootId?: string): Promise<TaskContext[]> {
   445â†’    if (!this.db) {
   446â†’      throw new DatabaseError('Database not initialized');
   447â†’    }
   448â†’
   449â†’    try {
   450â†’      if (rootId) {
   451â†’        // Verify root task exists
   452â†’        const rootTask = await this.getTaskById(rootId);
   453â†’        if (!rootTask) {
   454â†’          throw new TaskNotFoundError(rootId);
   455â†’        }
   456â†’
   457â†’        return await this.getTaskTree(rootId);
   458â†’      } else {
   459â†’        // Get all root tasks and their hierarchies
   460â†’        const rootTasks = await this.getRootTasks();
   461â†’        const result: TaskContext[] = [];
   462â†’
   463â†’        for (const rootTask of rootTasks) {
   464â†’          result.push(rootTask);
   465â†’          const children = await this.getTaskTree(rootTask.id);
   466â†’          result.push(...children);
   467â†’        }
   468â†’
   469â†’        return result;
   470â†’      }
   471â†’    } catch (error) {
   472â†’      if (error instanceof TaskNotFoundError) {
   473â†’        throw error;
   474â†’      }
   475â†’      throw new DatabaseError(`Failed to get task hierarchy: ${error instanceof Error ? error.message : String(error)}`);
   476â†’    }
   477â†’  }
   478â†’
   479â†’  /**
   480â†’   * Get task tree recursively
   481â†’   */
   482â†’  private async getTaskTree(parentId: string): Promise<TaskContext[]> {
   483â†’    if (!this.db) {
   484â†’      throw new DatabaseError('Database not initialized');
   485â†’    }
   486â†’
   487â†’    const children = await this.getChildTasks(parentId);
   488â†’    let result: TaskContext[] = [...children];
   489â†’
   490â†’    for (const child of children) {
   491â†’      const grandchildren = await this.getTaskTree(child.id);
   492â†’      result = [...result, ...grandchildren];
   493â†’    }
   494â†’
   495â†’    return result;
   496â†’  }
   497â†’
   498â†’  /**
   499â†’   * Validate temporal consistency of task hierarchy
   500â†’   */
   501â†’  async validateTemporalConsistency(taskId: string): Promise<boolean> {
   502â†’    if (!this.db) {
   503â†’      throw new DatabaseError('Database not initialized');
   504â†’    }
   505â†’
   506â†’    try {
   507â†’      const task = await this.getTaskById(taskId);
   508â†’      if (!task) {
   509â†’        throw new TaskNotFoundError(taskId);
   510â†’      }
   511â†’
   512â†’      // If no parent, temporal consistency is automatically valid
   513â†’      if (!task.parentTaskId) {
   514â†’        return true;
   515â†’      }
   516â†’
   517â†’      const parentTask = await this.getTaskById(task.parentTaskId);
   518â†’      if (!parentTask) {
   519â†’        // This should not happen in a consistent database with foreign keys
   520â†’        throw new TaskHierarchyError(`Parent task ${task.parentTaskId} not found for task ${taskId}`);
   521â†’      }
   522â†’
   523â†’      // Child task should not be created before parent
   524â†’      return task.createdAt >= parentTask.createdAt;
   525â†’    } catch (error) {
   526â†’      if (error instanceof TaskNotFoundError || error instanceof TaskHierarchyError) {
   527â†’        throw error;
   528â†’      }
   529â†’      throw new DatabaseError(`Failed to validate temporal consistency: ${error instanceof Error ? error.message : String(error)}`);
   530â†’    }
   531â†’  }
   532â†’
   533â†’  /**
   534â†’   * Execute operations in a transaction
   535â†’   */
   536â†’  async executeInTransaction<T>(operation: () => Promise<T>): Promise<T> {
   537â†’    if (!this.db) {
   538â†’      throw new DatabaseError('Database not initialized');
   539â†’    }
   540â†’
   541â†’    const db = this.db;
   542â†’
   543â†’    try {
   544â†’      await db.run('BEGIN TRANSACTION');
   545â†’      const result = await operation();
   546â†’      await db.run('COMMIT');
   547â†’      return result;
   548â†’    } catch (error) {
   549â†’      await db.run('ROLLBACK');
   550â†’      throw error;
   551â†’    }
   552â†’  }
   553â†’
   554â†’  /**
   555â†’   * Map database row to TaskContext object
   556â†’   */
   557â†’  private mapRowToTaskContext(row: any): TaskContext {
   558â†’    return {
   559â†’      id: row.id,
   560â†’      title: row.title,
   561â†’      description: row.description,
   562â†’      priority: row.priority as TaskPriority,
   563â†’      status: row.status as TaskStatus,
   564â†’      complexityScore: row.complexity_score,
   565â†’      estimatedDurationMinutes: row.estimated_duration_minutes,
   566â†’      requiredCapabilities: row.required_capabilities ? JSON.parse(row.required_capabilities) : null,
   567â†’      primaryPlatform: row.primary_platform as Platform,
   568â†’      platformRouting: row.platform_routing ? JSON.parse(row.platform_routing) : null,
