   354â†’  private serializeEmbedding(embedding: number[]): Buffer {
   355â†’    const buffer = Buffer.allocUnsafe(embedding.length * 4); // 4 bytes per float32
   356â†’    for (let i = 0; i < embedding.length; i++) {
