   394â†’  async calculateSimilarity(embedding1: number[], embedding2: number[]): Promise<number> {
   395â†’    if (embedding1.length !== embedding2.length) {
   396â†’      throw new Error('Embedding dimensions must match');
   397â†’    }
   398â†’
   399â†’    // Cosine similarity
   400â†’    let dotProduct = 0;
   401â†’    let norm1 = 0;
   402â†’    let norm2 = 0;
   403â†’
   404â†’    for (let i = 0; i < embedding1.length; i++) {
   405â†’      dotProduct += embedding1[i] * embedding2[i];
   406â†’      norm1 += embedding1[i] * embedding1[i];
   407â†’      norm2 += embedding2[i] * embedding2[i];
   408â†’    }
   409â†’
   410â†’    return dotProduct / (Math.sqrt(norm1) * Math.sqrt(norm2));
