#!/usr/bin/env npx ts-node

/**
 * DevFlow Schema Unification Migration Runner
 * Executes the database migration safely with validation and rollback capability
 */

import * as fs from 'fs';
import * as path from 'path';
import Database from 'better-sqlite3';

interface MigrationResult {
  success: boolean;
  rowsMigrated: number;
  errors: string[];
  rollbackAvailable: boolean;
}

class SchemaMigrationRunner {
  private db: Database.Database;
  private migrationPath: string;

  constructor(dbPath: string = './data/devflow.sqlite') {
    this.db = new Database(dbPath);
    this.migrationPath = path.join(__dirname, 'schema-unification-migration.sql');

    // Enable foreign keys and WAL mode for safety
    this.db.pragma('foreign_keys = ON');
    this.db.pragma('journal_mode = WAL');
  }

  /**
   * Pre-migration validation
   */
  validatePreMigration(): { valid: boolean; issues: string[] } {
    const issues: string[] = [];

    try {
      // Check if memory_blocks table exists (dependency)
      const memoryBlocksExists = this.db.prepare(`
        SELECT name FROM sqlite_master
        WHERE type='table' AND name='memory_blocks'
      `).get();

      if (!memoryBlocksExists) {
        issues.push('memory_blocks table does not exist - required for FK constraint');
      }

      // Check current schema state
      const currentSchema = this.db.prepare(`
        SELECT sql FROM sqlite_master
        WHERE type='table' AND name='memory_block_embeddings'
      `).get() as any;

      if (currentSchema) {
        console.log('Current schema detected:', currentSchema.sql);
      }

      // Create migration_log table if it doesn't exist
      this.db.exec(`
        CREATE TABLE IF NOT EXISTS migration_log (
          id INTEGER PRIMARY KEY AUTOINCREMENT,
          migration_name TEXT NOT NULL,
          completed_at DATETIME DEFAULT CURRENT_TIMESTAMP,
          affected_tables TEXT,
          rows_migrated INTEGER
        )
      `);

    } catch (error) {
      issues.push(`Pre-migration validation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    return {
      valid: issues.length === 0,
      issues
    };
  }

  /**
   * Execute the migration
   */
  async runMigration(): Promise<MigrationResult> {
    console.log('üöÄ Starting DevFlow Schema Unification Migration...');

    const validation = this.validatePreMigration();
    if (!validation.valid) {
      return {
        success: false,
        rowsMigrated: 0,
        errors: validation.issues,
        rollbackAvailable: false
      };
    }

    try {
      // Read migration SQL
      const migrationSQL = fs.readFileSync(this.migrationPath, 'utf8');

      // Count existing rows for verification
      let existingRows = 0;
      try {
        const countResult = this.db.prepare(`
          SELECT COUNT(*) as count FROM memory_block_embeddings
        `).get() as any;
        existingRows = countResult?.count || 0;
      } catch {
        // Table might not exist yet
        existingRows = 0;
      }

      console.log(`üìä Existing rows in memory_block_embeddings: ${existingRows}`);

      // Execute migration in transaction (already wrapped in migration SQL)
      console.log('üîÑ Executing migration script...');
      this.db.exec(migrationSQL);

      // Verify migration success
      const newCount = this.db.prepare(`
        SELECT COUNT(*) as count FROM memory_block_embeddings
      `).get() as any;

      const rowsMigrated = newCount?.count || 0;

      console.log(`‚úÖ Migration completed successfully`);
      console.log(`üìä Rows migrated: ${rowsMigrated}`);

      // Verify schema structure
      const finalSchema = this.db.prepare(`
        SELECT sql FROM sqlite_master
        WHERE type='table' AND name='memory_block_embeddings'
      `).get() as any;

      console.log('üìã Final schema:', finalSchema?.sql);

      return {
        success: true,
        rowsMigrated,
        errors: [],
        rollbackAvailable: true
      };

    } catch (error) {
      console.error('‚ùå Migration failed:', error);

      return {
        success: false,
        rowsMigrated: 0,
        errors: [error instanceof Error ? error.message : 'Unknown migration error'],
        rollbackAvailable: this.checkBackupExists()
      };
    }
  }

  /**
   * Check if backup table exists for rollback
   */
  private checkBackupExists(): boolean {
    try {
      const backup = this.db.prepare(`
        SELECT name FROM sqlite_master
        WHERE type='table' AND name='memory_block_embeddings_backup'
      `).get();
      return !!backup;
    } catch {
      return false;
    }
  }

  /**
   * Rollback migration if needed
   */
  rollback(): boolean {
    try {
      if (!this.checkBackupExists()) {
        console.error('‚ùå No backup available for rollback');
        return false;
      }

      console.log('üîÑ Rolling back migration...');

      this.db.exec(`
        BEGIN TRANSACTION;
        DROP TABLE IF EXISTS memory_block_embeddings;
        ALTER TABLE memory_block_embeddings_backup RENAME TO memory_block_embeddings;
        COMMIT;
      `);

      console.log('‚úÖ Rollback completed');
      return true;
    } catch (error) {
      console.error('‚ùå Rollback failed:', error);
      return false;
    }
  }

  /**
   * Clean up backup table after successful migration
   */
  cleanup(): void {
    try {
      this.db.exec('DROP TABLE IF EXISTS memory_block_embeddings_backup');
      console.log('üßπ Backup cleanup completed');
    } catch (error) {
      console.warn('‚ö†Ô∏è Cleanup warning:', error);
    }
  }

  close(): void {
    this.db.close();
  }
}

/**
 * Main execution
 */
async function main() {
  const args = process.argv.slice(2);
  const command = args[0] || 'migrate';

  const runner = new SchemaMigrationRunner();

  try {
    switch (command) {
      case 'migrate':
        const result = await runner.runMigration();
        if (result.success) {
          console.log('üéâ Schema unification migration completed successfully!');
          console.log(`üìä Summary: ${result.rowsMigrated} rows migrated`);

          // Ask user if they want to cleanup backup
          console.log('üí° Run with "cleanup" to remove backup table');
        } else {
          console.error('‚ùå Migration failed:');
          result.errors.forEach(error => console.error(`  - ${error}`));
          if (result.rollbackAvailable) {
            console.log('üí° Run with "rollback" to restore previous state');
          }
          process.exit(1);
        }
        break;

      case 'rollback':
        const success = runner.rollback();
        process.exit(success ? 0 : 1);
        break;

      case 'cleanup':
        runner.cleanup();
        break;

      case 'validate':
        const validation = runner.validatePreMigration();
        if (validation.valid) {
          console.log('‚úÖ Pre-migration validation passed');
        } else {
          console.log('‚ùå Pre-migration validation issues:');
          validation.issues.forEach(issue => console.log(`  - ${issue}`));
          process.exit(1);
        }
        break;

      default:
        console.log('Usage: npx ts-node run-schema-migration.ts [migrate|rollback|cleanup|validate]');
        process.exit(1);
    }
  } finally {
    runner.close();
  }
}

if (require.main === module) {
  main().catch(error => {
    console.error('Fatal error:', error);
    process.exit(1);
  });
}

export { SchemaMigrationRunner };

/**
 * DevFlow Unified Database Manager v1.0
 * Post-schema unification implementation
 * Addresses divergences between DatabaseManager and orchestrator services
 */

import Database from 'better-sqlite3';
import { randomUUID } from 'crypto';

export interface UnifiedMemoryBlockEmbedding {
  id: string;                    // Unified PK: unique row identifier
  memory_block_id: string;       // FK to memory_blocks
  model_id: string;              // Clear model identifier
  embedding_vector: Buffer;      // Unified embedding storage
  dimensions: number;            // Vector dimensions
  created_at: Date;              // Proper datetime tracking
  updated_at: Date;              // Update tracking
}

export interface MemoryBlock {
  id: string;
  content: string;
  type: string;
  timestamp: string;
  embedding?: Buffer;
}

export class UnifiedDatabaseManager {
  private db: Database.Database;

  constructor(dbPath: string = './data/devflow.sqlite') {
    this.db = new Database(dbPath);

    // Enable optimizations and safety features
    this.db.pragma('foreign_keys = ON');
    this.db.pragma('journal_mode = WAL');
    this.db.pragma('synchronous = NORMAL');
    this.db.pragma('cache_size = 1000');
    this.db.pragma('temp_store = memory');

    this.initializeSchema();
  }

  /**
   * Initialize unified schema (idempotent)
   */
  private initializeSchema(): void {
    // Ensure memory_blocks table exists
    this.db.exec(`
      CREATE TABLE IF NOT EXISTS memory_blocks (
        id TEXT PRIMARY KEY,
        content TEXT NOT NULL,
        type TEXT NOT NULL,
        timestamp TEXT NOT NULL,
        embedding BLOB
      )
    `);

    // Unified memory_block_embeddings schema
    this.db.exec(`
      CREATE TABLE IF NOT EXISTS memory_block_embeddings (
        id TEXT PRIMARY KEY,
        memory_block_id TEXT NOT NULL,
        model_id TEXT NOT NULL,
        embedding_vector BLOB NOT NULL,
        dimensions INTEGER NOT NULL,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (memory_block_id) REFERENCES memory_blocks(id) ON DELETE CASCADE
      )
    `);

    // Performance indexes
    this.db.exec(`
      CREATE INDEX IF NOT EXISTS idx_memory_embeddings_block_id ON memory_block_embeddings(memory_block_id);
      CREATE INDEX IF NOT EXISTS idx_memory_embeddings_model_id ON memory_block_embeddings(model_id);
      CREATE INDEX IF NOT EXISTS idx_memory_embeddings_created_at ON memory_block_embeddings(created_at);
    `);

    // Auto-update trigger for updated_at
    this.db.exec(`
      CREATE TRIGGER IF NOT EXISTS trigger_memory_embeddings_updated_at
      AFTER UPDATE ON memory_block_embeddings
      FOR EACH ROW
      BEGIN
        UPDATE memory_block_embeddings
        SET updated_at = CURRENT_TIMESTAMP
        WHERE id = NEW.id;
      END
    `);
  }

  /**
   * Store embedding with unified schema
   */
  storeEmbedding(
    memoryBlockId: string,
    modelId: string,
    embeddingVector: Buffer,
    dimensions: number
  ): string {
    const id = randomUUID();

    const stmt = this.db.prepare(`
      INSERT OR REPLACE INTO memory_block_embeddings (
        id, memory_block_id, model_id, embedding_vector, dimensions
      ) VALUES (?, ?, ?, ?, ?)
    `);

    stmt.run(id, memoryBlockId, modelId, embeddingVector, dimensions);
    return id;
  }

  /**
   * Retrieve embedding by memory block and model
   */
  getEmbedding(memoryBlockId: string, modelId: string): UnifiedMemoryBlockEmbedding | null {
    const stmt = this.db.prepare(`
      SELECT id, memory_block_id, model_id, embedding_vector, dimensions,
             created_at, updated_at
      FROM memory_block_embeddings
      WHERE memory_block_id = ? AND model_id = ?
      ORDER BY updated_at DESC
      LIMIT 1
    `);

    const row = stmt.get(memoryBlockId, modelId) as any;
    if (!row) return null;

    return {
      id: row.id,
      memory_block_id: row.memory_block_id,
      model_id: row.model_id,
      embedding_vector: row.embedding_vector,
      dimensions: row.dimensions,
      created_at: new Date(row.created_at),
      updated_at: new Date(row.updated_at)
    };
  }

  /**
   * Get all embeddings for a memory block
   */
  getEmbeddingsForBlock(memoryBlockId: string): UnifiedMemoryBlockEmbedding[] {
    const stmt = this.db.prepare(`
      SELECT id, memory_block_id, model_id, embedding_vector, dimensions,
             created_at, updated_at
      FROM memory_block_embeddings
      WHERE memory_block_id = ?
      ORDER BY updated_at DESC
    `);

    const rows = stmt.all(memoryBlockId) as any[];

    return rows.map(row => ({
      id: row.id,
      memory_block_id: row.memory_block_id,
      model_id: row.model_id,
      embedding_vector: row.embedding_vector,
      dimensions: row.dimensions,
      created_at: new Date(row.created_at),
      updated_at: new Date(row.updated_at)
    }));
  }

  /**
   * Store memory block
   */
  storeMemoryBlock(id: string, content: string, type: string, timestamp: string, embedding?: Buffer): void {
    const stmt = this.db.prepare(`
      INSERT OR REPLACE INTO memory_blocks (id, content, type, timestamp, embedding)
      VALUES (?, ?, ?, ?, ?)
    `);

    stmt.run(id, content, type, timestamp, embedding || null);
  }

  /**
   * Query memory blocks with embeddings
   */
  queryMemoryBlocks(options: {
    type?: string;
    limit?: number;
    orderBy?: 'timestamp' | 'created_at';
  } = {}): Array<MemoryBlock & { embeddings: UnifiedMemoryBlockEmbedding[] }> {
    const { type, limit = 50, orderBy = 'timestamp' } = options;

    let query = `
      SELECT mb.id, mb.content, mb.type, mb.timestamp, mb.embedding
      FROM memory_blocks mb
      WHERE 1=1
    `;
    const params: any[] = [];

    if (type) {
      query += ` AND mb.type = ?`;
      params.push(type);
    }

    query += ` ORDER BY mb.${orderBy} DESC LIMIT ?`;
    params.push(limit);

    const stmt = this.db.prepare(query);
    const blocks = stmt.all(...params) as any[];

    // Fetch embeddings for each block
    return blocks.map(block => ({
      id: block.id,
      content: block.content,
      type: block.type,
      timestamp: block.timestamp,
      embedding: block.embedding,
      embeddings: this.getEmbeddingsForBlock(block.id)
    }));
  }

  /**
   * Batch operations for performance
   */
  batchStoreEmbeddings(embeddings: Array<{
    memoryBlockId: string;
    modelId: string;
    embeddingVector: Buffer;
    dimensions: number;
  }>): string[] {
    const stmt = this.db.prepare(`
      INSERT OR REPLACE INTO memory_block_embeddings (
        id, memory_block_id, model_id, embedding_vector, dimensions
      ) VALUES (?, ?, ?, ?, ?)
    `);

    const transaction = this.db.transaction(() => {
      const ids: string[] = [];
      for (const embedding of embeddings) {
        const id = randomUUID();
        stmt.run(
          id,
          embedding.memoryBlockId,
          embedding.modelId,
          embedding.embeddingVector,
          embedding.dimensions
        );
        ids.push(id);
      }
      return ids;
    });

    return transaction();
  }

  /**
   * Search embeddings by similarity (requires cosine similarity implementation)
   */
  findSimilarEmbeddings(
    queryVector: Buffer,
    modelId: string,
    threshold: number = 0.7,
    limit: number = 10
  ): Array<UnifiedMemoryBlockEmbedding & { similarity: number }> {
    // Note: This is a simplified version. For production, implement proper vector similarity
    const stmt = this.db.prepare(`
      SELECT id, memory_block_id, model_id, embedding_vector, dimensions,
             created_at, updated_at
      FROM memory_block_embeddings
      WHERE model_id = ?
      ORDER BY updated_at DESC
      LIMIT ?
    `);

    const rows = stmt.all(modelId, limit * 2) as any[]; // Get more to filter by similarity

    // Simplified similarity calculation (replace with proper cosine similarity)
    const results = rows.map(row => ({
      id: row.id,
      memory_block_id: row.memory_block_id,
      model_id: row.model_id,
      embedding_vector: row.embedding_vector,
      dimensions: row.dimensions,
      created_at: new Date(row.created_at),
      updated_at: new Date(row.updated_at),
      similarity: 0.8 // Placeholder - implement actual similarity calculation
    })).filter(result => result.similarity >= threshold).slice(0, limit);

    return results;
  }

  /**
   * Get database statistics
   */
  getStats(): {
    memoryBlocks: number;
    embeddings: number;
    models: number;
    averageEmbeddingsPerBlock: number;
  } {
    const memoryBlocks = this.db.prepare('SELECT COUNT(*) as count FROM memory_blocks').get() as any;
    const embeddings = this.db.prepare('SELECT COUNT(*) as count FROM memory_block_embeddings').get() as any;
    const models = this.db.prepare('SELECT COUNT(DISTINCT model_id) as count FROM memory_block_embeddings').get() as any;

    const avgEmbeddings = memoryBlocks.count > 0 ? embeddings.count / memoryBlocks.count : 0;

    return {
      memoryBlocks: memoryBlocks.count,
      embeddings: embeddings.count,
      models: models.count,
      averageEmbeddingsPerBlock: Math.round(avgEmbeddings * 100) / 100
    };
  }

  /**
   * Cleanup and close database connection
   */
  close(): void {
    this.db.close();
  }
}

export default UnifiedDatabaseManager;

#!/usr/bin/env npx ts-node

/**
 * Test script for UnifiedDatabaseManager
 * Validates the new unified schema implementation
 */

import { UnifiedDatabaseManager } from '../src/database/UnifiedDatabaseManager';

async function testUnifiedDatabase() {
  console.log('üß™ Testing Unified Database Manager...');

  const db = new UnifiedDatabaseManager('./data/devflow.sqlite');

  try {
    // Test 1: Store a memory block
    console.log('\nüìù Test 1: Store memory block');
    const blockId = 'test_block_' + Date.now();
    db.storeMemoryBlock(
      blockId,
      'This is a test memory block for schema unification',
      'test',
      new Date().toISOString()
    );
    console.log('‚úÖ Memory block stored:', blockId);

    // Test 2: Store embedding with unified schema
    console.log('\nüßÆ Test 2: Store embedding');
    const embeddingData = Buffer.from(new Float32Array([0.1, 0.2, 0.3, 0.4]).buffer);
    const embeddingId = db.storeEmbedding(
      blockId,
      'text-embedding-3-small',
      embeddingData,
      4
    );
    console.log('‚úÖ Embedding stored:', embeddingId);

    // Test 3: Retrieve embedding
    console.log('\nüîç Test 3: Retrieve embedding');
    const retrievedEmbedding = db.getEmbedding(blockId, 'text-embedding-3-small');
    if (retrievedEmbedding) {
      console.log('‚úÖ Embedding retrieved:');
      console.log('  - ID:', retrievedEmbedding.id);
      console.log('  - Memory Block ID:', retrievedEmbedding.memory_block_id);
      console.log('  - Model ID:', retrievedEmbedding.model_id);
      console.log('  - Dimensions:', retrievedEmbedding.dimensions);
      console.log('  - Created:', retrievedEmbedding.created_at);
    } else {
      console.log('‚ùå Embedding not found');
    }

    // Test 4: Query memory blocks with embeddings
    console.log('\nüìä Test 4: Query memory blocks');
    const blocks = db.queryMemoryBlocks({ type: 'test', limit: 5 });
    console.log(`‚úÖ Found ${blocks.length} test blocks`);
    blocks.forEach(block => {
      console.log(`  - Block ${block.id}: ${block.embeddings.length} embeddings`);
    });

    // Test 5: Batch operations
    console.log('\n‚ö° Test 5: Batch embedding storage');
    const batchEmbeddings = [
      {
        memoryBlockId: blockId,
        modelId: 'text-embedding-3-large',
        embeddingVector: Buffer.from(new Float32Array([0.5, 0.6, 0.7, 0.8]).buffer),
        dimensions: 4
      },
      {
        memoryBlockId: blockId,
        modelId: 'text-embedding-ada-002',
        embeddingVector: Buffer.from(new Float32Array([0.9, 1.0, 1.1, 1.2]).buffer),
        dimensions: 4
      }
    ];

    const batchIds = db.batchStoreEmbeddings(batchEmbeddings);
    console.log(`‚úÖ Batch stored ${batchIds.length} embeddings:`, batchIds);

    // Test 6: Get all embeddings for block
    console.log('\nüîó Test 6: Get all embeddings for block');
    const allEmbeddings = db.getEmbeddingsForBlock(blockId);
    console.log(`‚úÖ Block has ${allEmbeddings.length} total embeddings:`);
    allEmbeddings.forEach(emb => {
      console.log(`  - ${emb.model_id}: ${emb.dimensions}D (${emb.created_at})`);
    });

    // Test 7: Database statistics
    console.log('\nüìà Test 7: Database statistics');
    const stats = db.getStats();
    console.log('‚úÖ Database stats:');
    console.log('  - Memory blocks:', stats.memoryBlocks);
    console.log('  - Embeddings:', stats.embeddings);
    console.log('  - Models:', stats.models);
    console.log('  - Avg embeddings/block:', stats.averageEmbeddingsPerBlock);

    console.log('\nüéâ All tests passed! Unified database schema is working correctly.');

  } catch (error) {
    console.error('‚ùå Test failed:', error);
    process.exit(1);
  } finally {
    db.close();
  }
}

if (require.main === module) {
  testUnifiedDatabase().catch(error => {
    console.error('Fatal test error:', error);
    process.exit(1);
  });
}

export { testUnifiedDatabase };

