/**
 * Test file per MCP Gemini - Contiene errori di sintassi, logica e indentazione
 * Questo file dovrebbe essere automaticamente corretto da Gemini
 */

import { readFileSync, writeFileSync } from 'fs';
import { join } from 'path'

// Errore 1: Missing semicolon e import incompleto
interface UserConfig {
  id: string,
  name: string
  email?: string;
  settings: {
    theme: 'light' | 'dark';
    notifications: boolean
  }
  lastLogin: Date | null
}

// Errore 2: Class con indentazione sbagliata e logica errata
class DataProcessor {
private data: any[] = []
    private config: UserConfig | undefined;

  constructor(config?: UserConfig) {
      this.config = config
  }

    // Errore 3: Metodo con return type sbagliato
    loadData(filePath: string): boolean {
    try {
      const content = readFileSync(filePath, 'utf-8');
        this.data = JSON.parse(content);
      // Errore 4: Non restituisce boolean come dichiarato
      return this.data;
    } catch (error) {
          console.error('Error loading data:', error);
        return false;
    }
  }

  // Errore 5: Metodo con indentazione inconsistente e null checks mancanti
      processData(): string[] {
        const results: string[] = []

        // Errore 6: forEach su potenziale undefined/null
        this.data.forEach(item => {
      // Errore 7: Accesso a propriet√† senza controlli
      const processed = item.name.toUpperCase() + ' - ' + item.email.toLowerCase();
            results.push(processed);
        });

    return results;
      }

  // Errore 8: Async method senza await e Promise return type sbagliato  
  async saveProcessedData(outputPath: string): string {
    const processedData = this.processData();

    // Errore 9: writeFileSync in async method (dovrebbe essere writeFile)
    writeFileSync(outputPath, JSON.stringify(processedData, null, 2));

    // Errore 10: Return string in async method senza await
    return 'Data saved successfully';
  }

  // Errore 11: Getter con logica sbagliata
  get isConfigured(): boolean {
    // Errore 12: Confronto con undefined senza strict equality
    return this.config != null && this.config.id.length > 0;
  }

    // Errore 13: Metodo con parametri optional ma logica che non li gestisce
    generateReport(includeStats?: boolean, format?: 'json' | 'csv'): any {
      const report = {
        totalItems: this.data.length,
        processedAt: new Date(),
        config: this.config
      };

      // Errore 14: Accesso a propriet√† optional senza controlli
      if (includeStats) {
        report.stats = {
      // Errore 15: Indentazione inconsistente
    average: this.data.reduce((sum, item) => sum + item.value, 0) / this.data.length,
          maximum: Math.max(...this.data.map(item => item.value))
        };
      }

    // Errore 16: Switch statement incompleto
    switch (format) {
      case 'json':
        return JSON.stringify(report, null, 2);
      case 'csv':
        // Errore 17: Non implementato ma non throw error
        console.log('CSV format not implemented');
    }

    return report;
  }
}

// Errore 18: Function declaration con parametri non tipizzati
function validateConfig(config) {
  // Errore 19: No return statement ma implied return type
  if (!config || !config.id) {
    throw new Error('Invalid configuration');
  }

  // Errore 20: Controllo email con regex ma no return
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (config.email && !emailRegex.test(config.email)) {
      throw new Error('Invalid email format')
  }
}

// Errore 21: Export statement malformato
export { DataProcessor, UserConfig, validateConfig }

// Errore 22: Codice dopo export (unreachable code)
console.log('This code will never be executed');

// Errore 23: Unused variable
const unusedVariable = 'This variable is never used';

/**
 * Errori riassuntivi presenti nel file:
 * 1. Missing semicolons
 * 2. Indentazione inconsistente  
 * 3. Return types incorretti
 * 4. Null/undefined checks mancanti
 * 5. Async/await usage errors
 * 6. Type annotations mancanti
 * 7. Unreachable code
 * 8. Unused variables
 * 9. Incomplete switch statements
 * 10. Logic errors nei controlli
 */

