import express from 'express';
import http from 'http';
import { Server } from 'socket.io';
import cors from 'cors';
import helmet from 'helmet';
import morgan from 'morgan';
import dotenv from 'dotenv';
import { syntheticRoutes } from './routes/synthetic';
import { memoryRoutes } from './routes/memory';
import { taskRoutes } from './routes/tasks';
import { sessionRoutes } from './routes/sessions';
import { setIo } from './ws/events';
import { authenticate } from './middleware/auth';
import { generalLimiter } from './middleware/rateLimit';
import { errorHandler } from './middleware/errorHandler';

dotenv.config();

const app = express();
const server = http.createServer(app);
const io = new Server(server, {
    cors: {
        origin: "*",
        methods: ["GET", "POST"]
    }
});

setIo(io);

app.use(helmet());
app.use(cors());
app.use(morgan('combined'));
app.use(express.json());
app.use(express.urlencoded({ extended: true }));
app.use(generalLimiter);

app.get('/health', (req, res) => {
    res.status(200).json({
        status: 'OK',
        service: 'devflow-orchestrator',
        timestamp: new Date().toISOString()
    });
});

// Metrics endpoint (lightweight, DB-based counters)
const dbSvc = require('./services/db');
app.get('/metrics', (req, res) => {
    try {
        const q = (sql: string) => {
            try { return dbSvc.db.prepare(sql).get()?.cnt ?? 0; } catch { return 0; }
        };
        const sessions = q("select count(*) as cnt from sessions");
        const tasks = q("select count(*) as cnt from task_contexts");
        const memory = q("select count(*) as cnt from memory_blocks");
        const synthetic = q("select count(*) as cnt from synthetic_usage");
        res.json({
            ok: true,
            timestamp: new Date().toISOString(),
            batching: {
                enabled: (process.env.DEVFLOW_SYNTHETIC_BATCH ?? '0') === '1',
                windowMs: Number(process.env.DEVFLOW_SYNTHETIC_BATCH_WINDOW_MS ?? 120),
                maxBatchSize: Number(process.env.DEVFLOW_SYNTHETIC_MAX_BATCH_SIZE ?? 8),
            },
            counts: { sessions, tasks, memory_blocks: memory, synthetic_usage: synthetic },
        });
    }
    catch (e) {
        res.status(500).json({ ok: false, error: String(e), timestamp: new Date().toISOString() });
    }
});

app.use('/api/synthetic', authenticate, syntheticRoutes);
app.use('/api/memory', authenticate, memoryRoutes);
app.use('/api/tasks', authenticate, taskRoutes);
app.use('/api/sessions', authenticate, sessionRoutes);

io.on('connection', (socket) => {
    console.log('Client connected:', socket.id);
    socket.on('disconnect', () => {
        console.log('Client disconnected:', socket.id);
    });
    socket.on('taskUpdate', (data) => {
        socket.broadcast.emit('taskUpdated', data);
    });
    socket.on('sessionUpdate', (data) => {
        socket.broadcast.emit('sessionUpdated', data);
    });
});

app.use('*', (req, res) => {
    res.status(404).json({
        error: 'Route not found'
    });
});

app.use(errorHandler);

const PORT = process.env.ORCHESTRATOR_PORT || 3005;
server.listen(PORT, () => {
    console.log(`DevFlow Orchestrator running on port ${PORT}`);
});

export { app, io };

