    54‚Üí  private chunkCode(content: string, metadata: FileMetadata): CodeChunk[] {
    55‚Üí    const lines = content.split('\n');
    56‚Üí    const chunks: CodeChunk[] = [];
    57‚Üí    const chunkSize = 50; // lines per chunk
    58‚Üí    const overlap = 5; // overlapping lines
    59‚Üí
    60‚Üí    for (let i = 0; i < lines.length; i += chunkSize - overlap) {
    61‚Üí      const chunkLines = lines.slice(i, Math.min(i + chunkSize, lines.length));
    62‚Üí      const chunkContent = chunkLines.join('\n');
    63‚Üí      
    64‚Üí      if (chunkContent.trim().length === 0) continue;
    65‚Üí
    66‚Üí      const chunkId = this.generateChunkId(metadata.filePath, i, i + chunkLines.length);
    67‚Üí      
    68‚Üí      chunks.push({
    69‚Üí        id: chunkId,
    70‚Üí        content: chunkContent,
    71‚Üí        filePath: metadata.filePath,
    72‚Üí        startLine: i + 1,
    73‚Üí        endLine: Math.min(i + chunkLines.length, lines.length),
    74‚Üí        language: metadata.language,
    75‚Üí        metadata: {
    76‚Üí          ...metadata,
    77‚Üí          chunkIndex: chunks.length
    78‚Üí        }
    79‚Üí      });
    80‚Üí
    81‚Üí      if (i + chunkSize >= lines.length) break;
    82‚Üí    }
    83‚Üí
    84‚Üí    return chunks;
    85‚Üí  }
