/**
 * GitHub Automation Startup Service
 * Initializes and manages the GitHub automation system for DevFlow
 */

import { TaskCompletionMonitor } from './task-completion-monitor';
import { Task } from '../task/task';
import fs from 'fs';
import path from 'path';

export class GitHubAutomationStartup {
  private monitor: TaskCompletionMonitor;
  private isRunning: boolean = false;
  private taskWatcher?: fs.FSWatcher;

  constructor() {
    // Initialize with production settings
    this.monitor = new TaskCompletionMonitor(
      3, // batchThreshold - commit after 3 tasks
      15000 // batchTimeout - commit after 15 seconds
    );
  }

  /**
   * Start the GitHub automation system
   */
  async start(): Promise<void> {
    if (this.isRunning) {
      console.log('GitHub automation already running');
      return;
    }

    try {
      console.log('üöÄ Starting GitHub automation system...');

      // Configure the monitor
      await this.monitor.configure({
        batchThreshold: 3,
        batchTimeout: 15000,
        triggerConditions: {
          onTaskComplete: true,
          onMacroTaskComplete: true,
          autoCommit: true,
          autoPush: true
        }
      });

      // Start watching for task completion events
      this.startTaskWatching();

      this.isRunning = true;
      console.log('‚úÖ GitHub automation system started');

    } catch (error) {
      console.error('‚ùå Failed to start GitHub automation:', error);
      throw error;
    }
  }

  /**
   * Stop the GitHub automation system
   */
  async stop(): Promise<void> {
    if (!this.isRunning) {
      console.log('GitHub automation not running');
      return;
    }

    try {
      console.log('üõë Stopping GitHub automation system...');

      // Stop file watcher
      if (this.taskWatcher) {
        this.taskWatcher.close();
        this.taskWatcher = undefined;
      }

      this.isRunning = false;
      console.log('‚úÖ GitHub automation system stopped');

    } catch (error) {
      console.error('‚ùå Error stopping GitHub automation:', error);
      throw error;
    }
  }

  /**
   * Manually trigger task completion
   */
  async triggerTaskCompletion(taskId: string, title: string, description?: string): Promise<void> {
    if (!this.isRunning) {
      throw new Error('GitHub automation system not running');
    }

    const task: Task = {
      id: taskId,
      title: title,
      description: description || '',
      status: 'completed',
      type: 'feature',
      category: 'devflow',
      priority: 'medium',
      progress: 100
    };

    this.monitor.emit('taskCompleted', task);
    console.log(`üìù Task completion triggered for: ${taskId}`);
  }

  /**
   * Trigger macro task completion
   */
  async triggerMacroTaskCompletion(tasks: Task[]): Promise<void> {
    if (!this.isRunning) {
      throw new Error('GitHub automation system not running');
    }

    this.monitor.emit('macroTaskCompleted', tasks);
    console.log(`üìù Macro task completion triggered for ${tasks.length} tasks`);
  }

  /**
   * Start watching for task completion events
   */
  private startTaskWatching(): void {
    const projectRoot = process.cwd();
    const taskStateFile = path.join(projectRoot, '.claude/state/current_task.json');

    // Watch for changes to current task file
    if (fs.existsSync(taskStateFile)) {
      this.taskWatcher = fs.watch(taskStateFile, (eventType) => {
        if (eventType === 'change') {
          this.checkTaskCompletion(taskStateFile);
        }
      });

      console.log('üëÅÔ∏è  Watching task state file for completion events');
    }
  }

  /**
   * Check if a task has been completed
   */
  private async checkTaskCompletion(taskStateFile: string): Promise<void> {
    try {
      const content = fs.readFileSync(taskStateFile, 'utf8');
      const taskData = JSON.parse(content);

      // Check if task progress indicates completion
      if (taskData.progress_percentage >= 100) {
        const task: Task = {
          id: taskData.task || 'unknown',
          title: taskData.task || 'Unknown Task',
          description: `Task completed with ${taskData.progress_percentage}% progress`,
          status: 'completed',
          type: 'feature',
          category: 'devflow',
          priority: 'medium',
          progress: taskData.progress_percentage
        };

        // Small delay to avoid rapid fire events
        setTimeout(() => {
          this.monitor.emit('taskCompleted', task);
        }, 1000);
      }

    } catch (error) {
      console.error('Error checking task completion:', error);
    }
  }

  /**
   * Get system status
   */
  getStatus(): { running: boolean; monitoring: boolean } {
    return {
      running: this.isRunning,
      monitoring: this.taskWatcher !== undefined
    };
  }
}

