#!/usr/bin/env npx ts-node

/**
 * GitHub Automation Daemon
 * Runs the GitHub automation system as a daemon process
 */

import { githubAutomation } from './github-automation-startup';
import fs from 'fs';
import path from 'path';

class GitHubAutomationDaemon {
  private pidFile: string;
  private isShuttingDown: boolean = false;

  constructor() {
    this.pidFile = path.join(process.cwd(), '.github-automation.pid');
  }

  async start(): Promise<void> {
    try {
      // Write PID file
      fs.writeFileSync(this.pidFile, process.pid.toString());

      console.log(`üìù GitHub Automation Daemon started (PID: ${process.pid})`);
      console.log(`üìÑ PID file: ${this.pidFile}`);

      // Start the GitHub automation system
      await githubAutomation.start();

      // Setup graceful shutdown
      this.setupGracefulShutdown();

      // Keep the process alive
      this.keepAlive();

    } catch (error) {
      console.error('‚ùå Failed to start GitHub Automation Daemon:', error);
      process.exit(1);
    }
  }

  private setupGracefulShutdown(): void {
    const shutdown = async (signal: string) => {
      if (this.isShuttingDown) return;
      this.isShuttingDown = true;

      console.log(`\nüìù Received ${signal}, shutting down GitHub Automation Daemon...`);

      try {
        // Stop GitHub automation
        await githubAutomation.stop();

        // Remove PID file
        if (fs.existsSync(this.pidFile)) {
          fs.unlinkSync(this.pidFile);
        }

        console.log('‚úÖ GitHub Automation Daemon shutdown complete');
        process.exit(0);

      } catch (error) {
        console.error('‚ùå Error during shutdown:', error);
        process.exit(1);
      }
    };

    process.on('SIGINT', () => shutdown('SIGINT'));
    process.on('SIGTERM', () => shutdown('SIGTERM'));
    process.on('SIGQUIT', () => shutdown('SIGQUIT'));

    // Handle uncaught exceptions
    process.on('uncaughtException', (error) => {
      console.error('‚ùå Uncaught exception in GitHub Automation Daemon:', error);
      shutdown('uncaughtException');
    });

    process.on('unhandledRejection', (reason, promise) => {
      console.error('‚ùå Unhandled rejection in GitHub Automation Daemon:', reason);
      shutdown('unhandledRejection');
    });
  }

  private keepAlive(): void {
    // Report status every 30 seconds
    setInterval(() => {
      const status = githubAutomation.getStatus();
      console.log(`üìù GitHub Automation Status - Running: ${status.running}, Monitoring: ${status.monitoring}`);
    }, 30000);

    // Keep process alive
    process.stdin.resume();
  }
}

// Start daemon if run directly
if (require.main === module) {
  const daemon = new GitHubAutomationDaemon();
  daemon.start().catch((error) => {
    console.error('‚ùå Failed to start daemon:', error);
    process.exit(1);
  });
}

export default GitHubAutomationDaemon;

