     1‚Üí/**
     2‚Üí * Task Authority System - COMETA-BRAIN-AUTHORITY-002
     3‚Üí * 
     4‚Üí * This module handles task management with proper SQL schema compliance,
     5‚Üí * immediate state synchronization, and enhanced error handling.
     6‚Üí */
     7‚Üí
     8‚Üíconst fs = require('fs').promises;
     9‚Üíconst path = require('path');
    10‚Üíconst sqlite3 = require('sqlite3').verbose();
    11‚Üí
    12‚Üí// Database configuration
    13‚Üíconst DB_PATH = process.env.DB_PATH || './.cometa/cometa.db';
    14‚Üíconst STATE_DIR = './.claude/state';
    15‚Üíconst DEVFLOW_DIR = './.devflow';
    16‚Üí
    17‚Üí/**
    18‚Üí * Initialize database connection
    19‚Üí */
    20‚Üífunction initializeDatabase() {
    21‚Üí    return new sqlite3.Database(DB_PATH, (err) => {
    22‚Üí        if (err) {
    23‚Üí            console.error('Database connection error:', err);
    24‚Üí        }
    25‚Üí    });
    26‚Üí}
    27‚Üí
    28‚Üí/**
    29‚Üí * Execute a database query with error handling
    30‚Üí * @param {sqlite3.Database} db - Database instance
    31‚Üí * @param {string} query - SQL query to execute
    32‚Üí * @param {Array} params - Query parameters
    33‚Üí * @returns {Promise<Array|Object>} Query results
    34‚Üí */
    35‚Üífunction executeQuery(db, query, params = []) {
    36‚Üí    return new Promise((resolve, reject) => {
    37‚Üí        db.all(query, params, (err, rows) => {
    38‚Üí            if (err) {
    39‚Üí                reject(new Error(`Database query failed: ${err.message}`));
    40‚Üí                return;
    41‚Üí            }
    42‚Üí            resolve(rows);
    43‚Üí        });
    44‚Üí    });
    45‚Üí}
    46‚Üí
    47‚Üí/**
    48‚Üí * Get active tasks from database
    49‚Üí * @param {sqlite3.Database} db - Database instance
    50‚Üí * @returns {Promise<Array>} Active tasks
    51‚Üí */
    52‚Üíasync function getActiveTasks(db) {
    53‚Üí    try {
    54‚Üí        const query = `
    55‚Üí            SELECT id, title, description, status, created_at
    56‚Üí            FROM task_contexts 
    57‚Üí            WHERE status IN ('planning', 'active', 'blocked')
    58‚Üí            ORDER BY created_at DESC
    59‚Üí        `;
    60‚Üí        return await executeQuery(db, query);
    61‚Üí    } catch (error) {
    62‚Üí        throw new Error(`Failed to fetch active tasks: ${error.message}`);
    63‚Üí    }
    64‚Üí}
    65‚Üí
    66‚Üí/**
    67‚Üí * Get completed tasks from database
    68‚Üí * @param {sqlite3.Database} db - Database instance
    69‚Üí * @returns {Promise<Array>} Completed tasks
    70‚Üí */
    71‚Üíasync function getCompletedTasks(db) {
    72‚Üí    try {
    73‚Üí        const query = `
    74‚Üí            SELECT id, title, description, status, created_at
    75‚Üí            FROM task_contexts 
    76‚Üí            WHERE status = 'completed'
    77‚Üí            ORDER BY created_at DESC
    78‚Üí        `;
    79‚Üí        return await executeQuery(db, query);
    80‚Üí    } catch (error) {
    81‚Üí        throw new Error(`Failed to fetch completed tasks: ${error.message}`);
    82‚Üí    }
    83‚Üí}
    84‚Üí
    85‚Üí/**
    86‚Üí * Calculate progress percentage
    87‚Üí * @param {number} completed - Number of completed tasks
    88‚Üí * @param {number} total - Total number of tasks
    89‚Üí * @returns {number} Progress percentage
    90‚Üí */
    91‚Üífunction calculateProgress(completed, total) {
    92‚Üí    if (total === 0) return 0;
    93‚Üí    return Math.round((completed / total) * 100);
    94‚Üí}
    95‚Üí
    96‚Üí/**
    97‚Üí * Ensure directory exists
    98‚Üí * @param {string} dirPath - Directory path
    99‚Üí */
   100‚Üíasync function ensureDirectory(dirPath) {
   101‚Üí    try {
   102‚Üí        await fs.access(dirPath);
   103‚Üí    } catch {
   104‚Üí        await fs.mkdir(dirPath, { recursive: true });
   105‚Üí    }
   106‚Üí}
   107‚Üí
   108‚Üí/**
   109‚Üí * Write state to file with immediate overwrite
   110‚Üí * @param {string} filePath - File path
   111‚Üí * @param {Object} data - Data to write
   112‚Üí */
   113‚Üíasync function writeStateFile(filePath, data) {
   114‚Üí    try {
   115‚Üí        await ensureDirectory(path.dirname(filePath));
   116‚Üí        await fs.writeFile(filePath, JSON.stringify(data, null, 2), 'utf8');
   117‚Üí    } catch (error) {
   118‚Üí        throw new Error(`Failed to write state file ${filePath}: ${error.message}`);
   119‚Üí    }
   120‚Üí}
   121‚Üí
   122‚Üí/**
   123‚Üí * Update Claude state file
   124‚Üí * @param {Object} taskData - Current task data
   125‚Üí */
   126‚Üíasync function updateClaudeState(taskData) {
   127‚Üí    const statePath = path.join(STATE_DIR, 'current_task.json');
   128‚Üí    await writeStateFile(statePath, taskData);
   129‚Üí}
   130‚Üí
   131‚Üí/**
   132‚Üí * Update DevFlow footer state
   133‚Üí * @param {Object} progressData - Progress information
   134‚Üí */
   135‚Üíasync function updateDevFlowState(progressData) {
   136‚Üí    const statePath = path.join(DEVFLOW_DIR, 'footer-state.json');
   137‚Üí    await writeStateFile(statePath, progressData);
   138‚Üí}
   139‚Üí
   140‚Üí/**
   141‚Üí * Main synchronization function
   142‚Üí */
   143‚Üíasync function syncTaskStates() {
   144‚Üí    let db;
   145‚Üí    
   146‚Üí    try {
   147‚Üí        // Initialize database
   148‚Üí        db = initializeDatabase();
   149‚Üí        
   150‚Üí        // Fetch tasks
   151‚Üí        const activeTasks = await getActiveTasks(db);
   152‚Üí        const completedTasks = await getCompletedTasks(db);
   153‚Üí        const totalTasks = activeTasks.length + completedTasks.length;
   154‚Üí        
   155‚Üí        // Calculate progress
   156‚Üí        const progress = calculateProgress(completedTasks.length, totalTasks);
   157‚Üí        
   158‚Üí        // Prepare state data
   159‚Üí        const currentTask = activeTasks.length > 0 ? activeTasks[0] : null;
   160‚Üí        const progressData = {
   161‚Üí            progress: `${progress}%`,
   162‚Üí            completed: completedTasks.length,
   163‚Üí            total: totalTasks,
   164‚Üí            timestamp: new Date().toISOString()
   165‚Üí        };
   166‚Üí        
   167‚Üí        // Update state files immediately
   168‚Üí        await updateClaudeState(currentTask || {});
   169‚Üí        await updateDevFlowState(progressData);
   170‚Üí        
   171‚Üí        console.log(`Task sync completed: ${completedTasks.length}/${totalTasks} tasks completed (${progress}%)`);
   172‚Üí        
   173‚Üí    } catch (error) {
   174‚Üí        console.error('Task synchronization failed:', error.message);
   175‚Üí        throw error;
   176‚Üí    } finally {
   177‚Üí        if (db) {
   178‚Üí            db.close();
   179‚Üí        }
   180‚Üí    }
   181‚Üí}
   182‚Üí
   183‚Üí/**
   184‚Üí * Run single synchronization cycle
   185‚Üí */
   186‚Üíasync function runSync() {
   187‚Üí    try {
   188‚Üí        await syncTaskStates();
   189‚Üí        console.log('State synchronization completed successfully');
   190‚Üí    } catch (error) {
   191‚Üí        console.error('Synchronization error:', error.message);
   192‚Üí        process.exit(1);
   193‚Üí    }
   194‚Üí}
   195‚Üí
   196‚Üí// Export for module usage
   197‚Üímodule.exports = {
   198‚Üí    syncTaskStates,
   199‚Üí    runSync,
   200‚Üí    getActiveTasks,
   201‚Üí    getCompletedTasks,
   202‚Üí    calculateProgress
   203‚Üí};
   204‚Üí
   205‚Üí// Run if called directly
   206‚Üíif (require.main === module) {
   207‚Üí    runSync();
   208‚Üí}

