/**
 * SQLite Storage for Cross-Platform Memory System
 * Generated by Synthetic Code Agent: DEVFLOW-MEMORY-001
 * WAL mode enabled for concurrent access
 */

import { UniversalContextFormat } from './universal-context-format';
import * as sqlite3 from 'sqlite3';
import * as path from 'path';

export class SQLiteStorage {
  private db: sqlite3.Database;
  private dbPath: string;

  constructor(dbPath?: string) {
    this.dbPath = dbPath || path.join(process.cwd(), 'data', 'devflow-memory.db');
    this.db = new sqlite3.Database(this.dbPath);
    this.initialize();
  }

  private initialize(): void {
    this.db.serialize(() => {
      // Enable WAL mode for better concurrent access
      this.db.run('PRAGMA journal_mode=WAL');
      this.db.run('PRAGMA synchronous=NORMAL');
      this.db.run('PRAGMA cache_size=10000');
      this.db.run('PRAGMA temp_store=memory');

      // Create contexts table with enhanced schema
      this.db.run(`
        CREATE TABLE IF NOT EXISTS contexts (
          id TEXT PRIMARY KEY,
          data BLOB NOT NULL,
          lastUpdated DATETIME NOT NULL,
          version INTEGER NOT NULL DEFAULT 1,
          platform TEXT,
          contextType TEXT,
          metadata TEXT,
          created DATETIME DEFAULT CURRENT_TIMESTAMP,
          tokensUsed INTEGER DEFAULT 0,
          costUsd REAL DEFAULT 0.0,
          qualityScore REAL DEFAULT 0.0
        )
      `);

      // Create indexes for performance
      this.db.run('CREATE INDEX IF NOT EXISTS idx_contexts_lastUpdated ON contexts(lastUpdated)');
      this.db.run('CREATE INDEX IF NOT EXISTS idx_contexts_platform ON contexts(platform)');
      this.db.run('CREATE INDEX IF NOT EXISTS idx_contexts_contextType ON contexts(contextType)');
      this.db.run('CREATE INDEX IF NOT EXISTS idx_contexts_version ON contexts(version)');

      // Create sync events table for audit trail
      this.db.run(`
        CREATE TABLE IF NOT EXISTS sync_events (
          id INTEGER PRIMARY KEY AUTOINCREMENT,
          contextId TEXT NOT NULL,
          eventType TEXT NOT NULL,
          timestamp DATETIME DEFAULT CURRENT_TIMESTAMP,
          source TEXT,
          data TEXT,
          FOREIGN KEY (contextId) REFERENCES contexts (id)
        )
      `);

      this.db.run('CREATE INDEX IF NOT EXISTS idx_sync_events_contextId ON sync_events(contextId)');
      this.db.run('CREATE INDEX IF NOT EXISTS idx_sync_events_timestamp ON sync_events(timestamp)');
    });
  }

  public async getContextById(id: string): Promise<UniversalContextFormat | null> {
    return new Promise((resolve, reject) => {
      this.db.get(
        'SELECT * FROM contexts WHERE id = ?',
        [id],
        (err, row) => {
          if (err) {
            reject(err);
          } else {
            resolve(row ? this.rowToContext(row) : null);
          }
        }
      );
    });
  }

  public async getAllContexts(): Promise<UniversalContextFormat[]> {
    return new Promise((resolve, reject) => {
      this.db.all(
        'SELECT * FROM contexts ORDER BY lastUpdated DESC',
        [],
        (err, rows) => {
          if (err) {
            reject(err);
          } else {
            resolve(rows.map(row => this.rowToContext(row)));
          }
        }
      );
    });
  }

  public async getContextsByPlatform(platform: string): Promise<UniversalContextFormat[]> {
    return new Promise((resolve, reject) => {
      this.db.all(
        'SELECT * FROM contexts WHERE platform = ? ORDER BY lastUpdated DESC',
        [platform],
        (err, rows) => {
          if (err) {
            reject(err);
          } else {
            resolve(rows.map(row => this.rowToContext(row)));
          }
        }
      );
    });
  }

  public async getContextsByType(contextType: string): Promise<UniversalContextFormat[]> {
    return new Promise((resolve, reject) => {
      this.db.all(
        'SELECT * FROM contexts WHERE contextType = ? ORDER BY lastUpdated DESC',
        [contextType],
        (err, rows) => {
          if (err) {
            reject(err);
          } else {
            resolve(rows.map(row => this.rowToContext(row)));
          }
        }
      );
    });
  }

  public async updateContext(context: UniversalContextFormat): Promise<void> {
    return new Promise((resolve, reject) => {
      const stmt = this.db.prepare(`
        INSERT OR REPLACE INTO contexts 
        (id, data, lastUpdated, version, platform, contextType, metadata, tokensUsed, costUsd, qualityScore) 
        VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
      `);

      stmt.run([
        context.id,
        context.data,
        context.lastUpdated.toISOString(),
        context.version,
        context.platform || null,
        context.contextType || null,
        JSON.stringify(context.metadata || {}),
        context.metadata?.tokens || 0,
        context.metadata?.cost || 0.0,
        context.metadata?.quality || 0.0
      ], function(err) {
        if (err) {
          reject(err);
        } else {
          resolve();
        }
      });

      stmt.finalize();
    });
  }

  public async deleteContext(id: string): Promise<void> {
    return new Promise((resolve, reject) => {
      this.db.run(
        'DELETE FROM contexts WHERE id = ?',
        [id],
        function(err) {
          if (err) {
            reject(err);
          } else {
            resolve();
          }
        }
      );
    });
  }

  public async logSyncEvent(contextId: string, eventType: string, source: string, data?: any): Promise<void> {
    return new Promise((resolve, reject) => {
      this.db.run(
        'INSERT INTO sync_events (contextId, eventType, source, data) VALUES (?, ?, ?, ?)',
        [contextId, eventType, source, data ? JSON.stringify(data) : null],
        function(err) {
          if (err) {
            reject(err);
          } else {
            resolve();
          }
        }
      );
    });
  }

  public async getStorageStats(): Promise<{
    totalContexts: number;
    totalSize: number;
    contextsByPlatform: { [platform: string]: number };
    contextsByType: { [type: string]: number };
    avgCost: number;
    avgQuality: number;
  }> {
    return new Promise((resolve, reject) => {
      const stats = {
        totalContexts: 0,
        totalSize: 0,
        contextsByPlatform: {} as { [platform: string]: number },
        contextsByType: {} as { [type: string]: number },
        avgCost: 0,
        avgQuality: 0
      };

      this.db.serialize(() => {
        // Get total contexts and averages
        this.db.get(`
          SELECT 
            COUNT(*) as total,
            AVG(costUsd) as avgCost,
            AVG(qualityScore) as avgQuality
          FROM contexts
        `, [], (err, row) => {
          if (err) {
            reject(err);
            return;
          }
          stats.totalContexts = row.total;
          stats.avgCost = row.avgCost || 0;
          stats.avgQuality = row.avgQuality || 0;
        });

        // Get contexts by platform
        this.db.all(`
          SELECT platform, COUNT(*) as count 
          FROM contexts 
          WHERE platform IS NOT NULL 
          GROUP BY platform
        `, [], (err, rows) => {
          if (err) {
            reject(err);
            return;
          }
          rows.forEach((row: any) => {
            stats.contextsByPlatform[row.platform] = row.count;
          });
        });

        // Get contexts by type
        this.db.all(`
          SELECT contextType, COUNT(*) as count 
          FROM contexts 
          WHERE contextType IS NOT NULL 
          GROUP BY contextType
        `, [], (err, rows) => {
          if (err) {
            reject(err);
            return;
          }
          rows.forEach((row: any) => {
            stats.contextsByType[row.contextType] = row.count;
          });

          resolve(stats);
        });
      });
    });
  }

  public async cleanup(olderThanDays: number = 30): Promise<number> {
    return new Promise((resolve, reject) => {
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - olderThanDays);

      this.db.run(
        'DELETE FROM contexts WHERE lastUpdated < ?',
        [cutoffDate.toISOString()],
        function(err) {
          if (err) {
            reject(err);
          } else {
            resolve(this.changes || 0);
          }
        }
      );
    });
  }

  private rowToContext(row: any): UniversalContextFormat {
    return {
      id: row.id,
      data: row.data,
      lastUpdated: new Date(row.lastUpdated),
      version: row.version,
      platform: row.platform,
      contextType: row.contextType,
      metadata: row.metadata ? JSON.parse(row.metadata) : undefined
    };
  }

  public close(): void {
    this.db.close((err) => {
      if (err) {
        console.error('Error closing database:', err);
      } else {
        console.log('üì¶ SQLite database connection closed');
      }
    });
  }

  public async vacuum(): Promise<void> {
    return new Promise((resolve, reject) => {
      this.db.run('VACUUM', (err) => {
        if (err) {
          reject(err);
        } else {
          console.log('üßπ Database vacuumed successfully');
          resolve();
        }
      });
    });
  }
}

/**
 * Cross-Platform Memory System - Comprehensive Test Suite
 * Generated by Synthetic Code Agent: DEVFLOW-MEMORY-001
 * Tests for Phase 1 completion validation
 */

import { CrossPlatformSynchronizer } from './cross-platform-synchronizer';
import { ConflictResolver } from './conflict-resolver';
import { ContextOptimizer } from './context-optimizer';
import { SQLiteStorage } from './sqlite-storage';
import { UniversalContextFormat } from './universal-context-format';

// Test Suite
async function runCrossPlatformMemoryTests() {
  console.log('üß™ RUNNING CROSS-PLATFORM MEMORY SYSTEM TESTS');
  console.log('==============================================');

  let testsPassed = 0;
  let totalTests = 0;

  // Test 1: SQLite Storage
  console.log('\nüì¶ Testing SQLite Storage...');
  totalTests++;
  try {
    const storage = new SQLiteStorage(':memory:');

    const testContext: UniversalContextFormat = {
      id: 'test-1',
      data: { message: 'Hello DevFlow', code: 'function test() { return true; }' },
      lastUpdated: new Date(),
      version: 1,
      platform: 'synthetic',
      contextType: 'code',
      metadata: { tokens: 100, cost: 0.01, quality: 0.8 }
    };

    await storage.updateContext(testContext);
    const retrieved = await storage.getContextById('test-1');

    if (retrieved && retrieved.id === testContext.id && retrieved.platform === 'synthetic') {
      console.log('   ‚úÖ SQLite Storage: PASSED');
      testsPassed++;
    } else {
      console.log('   ‚ùå SQLite Storage: FAILED - Context mismatch');
    }

    storage.close();
  } catch (error) {
    console.log('   ‚ùå SQLite Storage: FAILED -', error.message);
  }

  // Test 2: Context Optimizer
  console.log('\nüóúÔ∏è  Testing Context Optimizer...');
  totalTests++;
  try {
    const optimizer = new ContextOptimizer();

    const largeContext: UniversalContextFormat = {
      id: 'test-2',
      data: { 
        largeText: 'x'.repeat(10000),
        codeSnippets: Array(100).fill({ code: 'function example() { return "test"; }' }),
        metadata: { description: 'Large test context for compression testing' }
      },
      lastUpdated: new Date(),
      version: 1
    };

    const compressed = optimizer.compress(largeContext);
    const decompressed = optimizer.decompress(compressed);

    const stats = optimizer.getOptimizationStats(compressed);

    if (stats.isCompressed && stats.compressionRatio && stats.compressionRatio < 0.8) {
      console.log(`   ‚úÖ Context Optimizer: PASSED (${(stats.compressionRatio * 100).toFixed(1)}% compression)`);
      testsPassed++;
    } else {
      console.log('   ‚ùå Context Optimizer: FAILED - Insufficient compression');
    }
  } catch (error) {
    console.log('   ‚ùå Context Optimizer: FAILED -', error.message);
  }

  // Test 3: Conflict Resolver
  console.log('\nüîÑ Testing Conflict Resolver...');
  totalTests++;
  try {
    const resolver = new ConflictResolver();

    const context1: UniversalContextFormat = {
      id: 'test-3',
      data: { feature: 'login', status: 'in-progress', author: 'alice' },
      lastUpdated: new Date(Date.now() - 1000),
      version: 1
    };

    const context2: UniversalContextFormat = {
      id: 'test-3',
      data: { feature: 'login', status: 'completed', reviewer: 'bob' },
      lastUpdated: new Date(),
      version: 2
    };

    const resolved = await resolver.resolve(context1, context2, 'merge');

    if (resolved.data.author === 'alice' && resolved.data.reviewer === 'bob' && resolved.data.status === 'completed') {
      console.log('   ‚úÖ Conflict Resolver: PASSED - Merge strategy worked');
      testsPassed++;
    } else {
      console.log('   ‚ùå Conflict Resolver: FAILED - Merge failed');
    }
  } catch (error) {
    console.log('   ‚ùå Conflict Resolver: FAILED -', error.message);
  }

  // Test 4: Cross-Platform Synchronizer
  console.log('\n‚ö° Testing Cross-Platform Synchronizer...');
  totalTests++;
  try {
    const synchronizer = new CrossPlatformSynchronizer(':memory:');

    const context: UniversalContextFormat = {
      id: 'test-4',
      data: { 
        task: 'Implement Phase 1 completion',
        platform: 'synthetic',
        status: 'completed'
      },
      lastUpdated: new Date(),
      version: 1,
      platform: 'synthetic',
      contextType: 'code'
    };

    const startTime = Date.now();
    await synchronizer.synchronize(context);
    const syncTime = Date.now() - startTime;

    const retrieved = await synchronizer.getContextById('test-4');
    const syncStatus = synchronizer.getSyncStatus();

    if (retrieved && retrieved.id === context.id && syncTime < 200) {
      console.log(`   ‚úÖ Cross-Platform Synchronizer: PASSED (${syncTime}ms < 200ms target)`);
      testsPassed++;
    } else {
      console.log(`   ‚ùå Cross-Platform Synchronizer: FAILED - Sync time ${syncTime}ms or retrieval failed`);
    }

    synchronizer.close();
  } catch (error) {
    console.log('   ‚ùå Cross-Platform Synchronizer: FAILED -', error.message);
  }

  // Test 5: Platform-specific Optimization
  console.log('\nüéØ Testing Platform-specific Optimization...');
  totalTests++;
  try {
    const optimizer = new ContextOptimizer();

    const context: UniversalContextFormat = {
      id: 'test-5',
      data: { 
        code: 'function example() { return "synthetic"; }',
        architecture: 'microservices design pattern',
        reasoning: 'complex analysis of trade-offs',
        implementation: 'TypeScript with proper typing'
      },
      lastUpdated: new Date(),
      version: 1
    };

    const syntheticOptimized = await optimizer.optimizeForPlatform(context, 'synthetic');
    const openrouterOptimized = await optimizer.optimizeForPlatform(context, 'openrouter');

    // Synthetic should be compressed (flat fee efficiency)
    // OpenRouter should not be compressed (premium model processing)
    const syntheticStats = optimizer.getOptimizationStats(syntheticOptimized);
    const openrouterStats = optimizer.getOptimizationStats(openrouterOptimized);

    if (syntheticStats.isCompressed && !openrouterStats.isCompressed) {
      console.log('   ‚úÖ Platform-specific Optimization: PASSED');
      testsPassed++;
    } else {
      console.log('   ‚ùå Platform-specific Optimization: FAILED - Optimization rules not applied correctly');
    }
  } catch (error) {
    console.log('   ‚ùå Platform-specific Optimization: FAILED -', error.message);
  }

  // Test 6: End-to-End Integration
  console.log('\nüöÄ Testing End-to-End Integration...');
  totalTests++;
  try {
    const synchronizer = new CrossPlatformSynchronizer(':memory:');

    // Simulate multi-platform workflow
    const contexts = [
      {
        id: 'workflow-1',
        data: { step: 'architecture', platform: 'claude-code', decision: 'microservices' },
        lastUpdated: new Date(Date.now() - 3000),
        version: 1,
        platform: 'claude-code',
        contextType: 'reasoning' as const
      },
      {
        id: 'workflow-2',
        data: { step: 'implementation', platform: 'synthetic', code: 'class Service {}' },
        lastUpdated: new Date(Date.now() - 2000),
        version: 1,
        platform: 'synthetic',
        contextType: 'code' as const
      },
      {
        id: 'workflow-3',
        data: { step: 'optimization', platform: 'openrouter', analysis: 'performance review' },
        lastUpdated: new Date(Date.now() - 1000),
        version: 1,
        platform: 'openrouter',
        contextType: 'reasoning' as const
      }
    ];

    // Synchronize all contexts
    for (const context of contexts) {
      await synchronizer.synchronize(context);
    }

    // Retrieve all contexts
    const allContexts = await synchronizer.getAllContexts();

    if (allContexts.length === 3 && allContexts.every(c => c.id.startsWith('workflow-'))) {
      console.log('   ‚úÖ End-to-End Integration: PASSED - Multi-platform workflow completed');
      testsPassed++;
    } else {
      console.log('   ‚ùå End-to-End Integration: FAILED - Context count or retrieval issue');
    }

    synchronizer.close();
  } catch (error) {
    console.log('   ‚ùå End-to-End Integration: FAILED -', error.message);
  }

  // Test Results
  console.log('\nüìä TEST RESULTS');
  console.log('================');
  console.log(`Tests Passed: ${testsPassed}/${totalTests}`);
  console.log(`Success Rate: ${(testsPassed/totalTests*100).toFixed(0)}%`);

  if (testsPassed === totalTests) {
    console.log('üéâ ALL TESTS PASSED - PHASE 1 CROSS-PLATFORM MEMORY SYSTEM READY!');
  } else {
    console.log('‚ö†Ô∏è  SOME TESTS FAILED - Review implementation before Phase 1 completion');
  }

  return testsPassed === totalTests;
}

// Export test runner
export { runCrossPlatformMemoryTests };

// Auto-run if executed directly
if (require.main === module) {
  runCrossPlatformMemoryTests();
}

