import { SyntheticGateway } from '@devflow/synthetic';
import { loadSyntheticEnv } from '@devflow/shared';
import * as fs from 'fs';
import * as path from 'path';

interface SyntheticCommandOptions {
  agent?: 'code' | 'reasoning' | 'context' | 'auto';
  maxTokens?: number;
  temperature?: number;
  output?: 'chat' | 'file';
  filename?: string;
}

export class SyntheticCommand {
  private gateway: SyntheticGateway | null = null;
  private isInitialized = false;

  private async initialize(): Promise<boolean> {
    if (this.isInitialized) return true;

    try {
      // Load environment and check if Synthetic.new is configured
      const env = loadSyntheticEnv();
      if (!env.SYNTHETIC_API_KEY) {
        console.error('‚ùå SYNTHETIC_API_KEY not found in environment variables');
        return false;
      }

      this.gateway = new SyntheticGateway({
        apiKey: env.SYNTHETIC_API_KEY,
        baseUrl: env.SYNTHETIC_BASE_URL,
        timeoutMs: env.SYNTHETIC_TIMEOUT_MS,
      });

      this.isInitialized = true;
      return true;
    } catch (error) {
      console.error('‚ùå Failed to initialize Synthetic.new:', error);
      return false;
    }
  }

  async execute(prompt: string, options: SyntheticCommandOptions = {}): Promise<string> {
    if (!await this.initialize()) {
      return '‚ùå Synthetic.new is not properly configured. Please check your SYNTHETIC_API_KEY environment variable.';
    }

    if (!this.gateway) {
      return '‚ùå Synthetic.new gateway not available.';
    }

    try {
      console.log('üß† Synthetic.new Processing...');
      console.log(`   Agent: ${options.agent || 'auto'}`);
      console.log(`   Max Tokens: ${options.maxTokens || 'default'}`);

      const startTime = Date.now();

      const request = {
        title: 'Claude Code Request',
        description: prompt,
        messages: [{
          role: 'user' as const,
          content: prompt,
        }],
        maxTokens: options.maxTokens,
        temperature: options.temperature,
      };

      let result;
      if (options.agent && options.agent !== 'auto') {
        // Use specific agent
        result = await this.gateway.processWithAgent(options.agent, request);
      } else {
        // Auto-select agent
        result = await this.gateway.process(request);
      }

      const executionTime = Date.now() - startTime;

      // Format response
      let response = `ü§ñ **Synthetic.new Response**\n`;
      response += `**Agent**: ${result.agent} (${result.model})\n`;
      response += `**Time**: ${executionTime}ms | **Tokens**: ${result.tokensUsed}\n`;

      if (result.classification) {
        response += `**Classification**: ${result.classification.type} (${(result.classification.confidence * 100).toFixed(0)}% confidence)\n`;
      }

      response += `\n---\n\n${result.text}`;

      // Handle output options
      if (options.output === 'file' && options.filename) {
        await this.writeToFile(result.text, options.filename);
        response += `\n\nüìÅ **Output written to**: ${options.filename}`;
      }

      // Log cost information
      const costStats = this.gateway.getCostStats();
      if (costStats.totalRequests > 0) {
        response += `\n\nüí∞ **Cost Info**: ${costStats.totalRequests} requests, $${costStats.monthlyCostUsd}/month flat fee`;
      }

      return response;

    } catch (error) {
      console.error('‚ùå Synthetic.new execution failed:', error);
      return `‚ùå Synthetic.new error: ${error instanceof Error ? error.message : 'Unknown error'}`;
    }
  }

  async getStatus(): Promise<string> {
    if (!await this.initialize()) {
      return '‚ùå Synthetic.new not configured';
    }

    if (!this.gateway) {
      return '‚ùå Gateway unavailable';
    }

    const agents = this.gateway.getAvailableAgents();
    const costStats = this.gateway.getCostStats();

    let status = `ü§ñ **Synthetic.new Status**\n`;
    status += `**Status**: ‚úÖ Operational\n`;
    status += `**Agents**: ${Object.keys(agents).join(', ')}\n`;
    status += `**Requests**: ${costStats.totalRequests}\n`;
    status += `**Tokens**: ${costStats.totalTokens.toLocaleString()}\n`;
    status += `**Cost**: $${costStats.monthlyCostUsd}/month (flat fee)\n`;

    return status;
  }

  async getHelp(): Promise<string> {
    return `ü§ñ **Synthetic.new Commands**

**Basic Usage**:
\`/synthetic <prompt>\` - Auto-select best agent
\`/synthetic-code <prompt>\` - Use code generation agent  
\`/synthetic-reasoning <prompt>\` - Use reasoning agent
\`/synthetic-context <prompt>\` - Use large context agent

**Advanced Options**:
\`/synthetic <prompt> --agent=code --maxTokens=500\`
\`/synthetic <prompt> --output=file --filename=result.md\`

**Available Agents**:
- **code**: Specialized in implementation, APIs, refactoring
- **reasoning**: Specialized in analysis, decisions, architecture  
- **context**: Specialized in large codebase analysis, documentation
- **auto**: Automatically selects the best agent (default)

**Examples**:
\`/synthetic "Create a TypeScript function to validate email addresses"\`
\`/synthetic-reasoning "Compare microservices vs monolith for e-commerce"\`
\`/synthetic-code "Implement JWT authentication middleware" --maxTokens=800\`
\`/synthetic "Analyze this codebase for performance issues" --agent=context\`

**Status Commands**:
\`/synthetic-status\` - Show current status and usage
\`/synthetic-cost\` - Show cost breakdown and savings`;
  }

  private async writeToFile(content: string, filename: string): Promise<void> {
    try {
      const fullPath = path.resolve(filename);
      await fs.promises.writeFile(fullPath, content, 'utf-8');
    } catch (error) {
      throw new Error(`Failed to write file ${filename}: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }
}

// Export singleton instance
export const syntheticCommand = new SyntheticCommand();

import { SyntheticGateway } from '@devflow/synthetic';
import { Edit, MultiEdit, Read, Write } from '../tools/file-operations.js';
import * as path from 'path';
import * as fs from 'fs';

export interface CodeModificationRequest {
  readonly task: string;
  readonly files: string[];
  readonly constraints?: string[];
  readonly dryRun?: boolean;
  readonly requireApproval?: boolean;
}

export interface CodeModificationResult {
  readonly success: boolean;
  readonly changes: FileChange[];
  readonly summary: string;
  readonly tokensUsed: number;
  readonly agent: string;
  readonly errors?: string[];
}

export interface FileChange {
  readonly filepath: string;
  readonly action: 'create' | 'modify' | 'delete';
  readonly oldContent?: string;
  readonly newContent?: string;
  readonly changes?: Array<{
    oldString: string;
    newString: string;
    lineNumber?: number;
  }>;
}

export class AutonomousCodeModifier {
  private readonly gateway: SyntheticGateway;
  private readonly workingDirectory: string;

  constructor(gateway: SyntheticGateway, workingDirectory: string = process.cwd()) {
    this.gateway = gateway;
    this.workingDirectory = workingDirectory;
  }

  /**
   * Execute autonomous code modifications based on natural language request
   */
  async modifyCode(request: CodeModificationRequest): Promise<CodeModificationResult> {
    console.log('ü§ñ Autonomous Code Modification Started...');
    console.log(`   Task: ${request.task}`);
    console.log(`   Files: ${request.files.length} target files`);
    console.log(`   Mode: ${request.dryRun ? 'DRY RUN' : 'LIVE EXECUTION'}`);

    try {
      // Step 1: Read existing files and analyze codebase
      const fileContents = await this.readTargetFiles(request.files);

      // Step 2: Generate modification plan using Synthetic.new
      const modificationPlan = await this.generateModificationPlan(request, fileContents);

      // Step 3: Execute modifications (or dry run)
      const changes = request.dryRun 
        ? await this.simulateChanges(modificationPlan)
        : await this.executeChanges(modificationPlan, request.requireApproval);

      // Step 4: Generate summary and results
      const result: CodeModificationResult = {
        success: true,
        changes,
        summary: modificationPlan.summary,
        tokensUsed: modificationPlan.tokensUsed,
        agent: modificationPlan.agent,
      };

      console.log(`‚úÖ Autonomous modification completed: ${changes.length} files affected`);
      return result;

    } catch (error) {
      console.error('‚ùå Autonomous modification failed:', error);
      return {
        success: false,
        changes: [],
        summary: `Failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        tokensUsed: 0,
        agent: 'none',
        errors: [error instanceof Error ? error.message : 'Unknown error'],
      };
    }
  }

  /**
   * Generate a structured modification plan using Synthetic.new
   */
  private async generateModificationPlan(
    request: CodeModificationRequest, 
    fileContents: Record<string, string>
  ) {
    const contextualPrompt = this.buildContextualPrompt(request, fileContents);

    const planRequest = {
      title: 'Autonomous Code Modification Plan',
      description: contextualPrompt,
      messages: [{
        role: 'user' as const,
        content: contextualPrompt,
      }],
      maxTokens: 1500,
      temperature: 0.1, // Low temperature for precise code generation
    };

    // Use code agent for implementation tasks
    const result = await this.gateway.processWithAgent('code', planRequest);

    return {
      plan: result.text,
      summary: this.extractSummary(result.text),
      operations: this.parseModificationOperations(result.text),
      tokensUsed: result.tokensUsed || 0,
      agent: result.agent,
    };
  }

  private buildContextualPrompt(request: CodeModificationRequest, fileContents: Record<string, string>): string {
    let prompt = `AUTONOMOUS CODE MODIFICATION REQUEST

TASK: ${request.task}

CONSTRAINTS:
${(request.constraints || []).map(c => `- ${c}`).join('\n')}
- Maintain existing code style and patterns
- Preserve all existing functionality unless explicitly modifying it
- Add appropriate error handling and validation
- Include helpful comments for complex logic

CURRENT CODEBASE CONTEXT:
`;

    // Add file contents with proper formatting
    Object.entries(fileContents).forEach(([filepath, content]) => {
      prompt += `\n--- ${filepath} ---\n${content}\n`;
    });

    prompt += `\nINSTRUCTIONS:
1. Analyze the existing code structure and patterns
2. Generate specific file modifications to accomplish the task
3. Format your response as structured JSON with this format:

\`\`\`json
{
  "summary": "Brief description of changes made",
  "modifications": [
    {
      "file": "path/to/file.ts",
      "action": "modify|create|delete", 
      "changes": [
        {
          "old": "code to replace (exact match required)",
          "new": "replacement code",
          "description": "what this change does"
        }
      ]
    }
  ]
}
\`\`\`

IMPORTANT: 
- Provide exact string matches for "old" code (including whitespace)
- Ensure "new" code maintains proper indentation and formatting
- Test logic should be comprehensive and handle edge cases
- Only modify what's necessary to complete the task

Generate the modification plan now:`;

    return prompt;
  }

  private async readTargetFiles(files: string[]): Promise<Record<string, string>> {
    const contents: Record<string, string> = {};

    for (const file of files) {
      try {
        const fullPath = path.resolve(this.workingDirectory, file);
        if (await this.fileExists(fullPath)) {
          contents[file] = await fs.promises.readFile(fullPath, 'utf-8');
        } else {
          console.log(`‚ö†Ô∏è File not found: ${file} (will be created)`);
          contents[file] = ''; // Empty content for new files
        }
      } catch (error) {
        console.warn(`Warning: Could not read ${file}:`, error);
        contents[file] = '';
      }
    }

    return contents;
  }

  private parseModificationOperations(planText: string): any[] {
    try {
      // Extract JSON from the response
      const jsonMatch = planText.match(/```json\n([\s\S]*?)\n```/);
      if (!jsonMatch) {
        throw new Error('No JSON modification plan found in response');
      }

      const parsed = JSON.parse(jsonMatch[1]);
      return parsed.modifications || [];
    } catch (error) {
      console.error('Failed to parse modification plan:', error);
      return [];
    }
  }

  private extractSummary(planText: string): string {
    try {
      const jsonMatch = planText.match(/```json\n([\s\S]*?)\n```/);
      if (jsonMatch) {
        const parsed = JSON.parse(jsonMatch[1]);
        return parsed.summary || 'Code modifications generated';
      }
    } catch (error) {
      // Fallback: extract first paragraph
      const lines = planText.split('\n');
      return lines.find(line => line.trim().length > 10) || 'Code modifications generated';
    }

    return 'Code modifications generated';
  }

  private async simulateChanges(plan: any): Promise<FileChange[]> {
    const changes: FileChange[] = [];

    for (const operation of plan.operations) {
      const change: FileChange = {
        filepath: operation.file,
        action: operation.action,
        changes: operation.changes || [],
      };

      if (operation.action === 'create') {
        change.newContent = this.generateNewFileContent(operation.changes);
      }

      changes.push(change);
    }

    return changes;
  }

  private async executeChanges(plan: any, requireApproval: boolean = false): Promise<FileChange[]> {
    const changes: FileChange[] = [];

    for (const operation of plan.operations) {
      if (requireApproval) {
        const approved = await this.requestApproval(operation);
        if (!approved) {
          console.log(`‚è≠Ô∏è Skipping ${operation.file} (not approved)`);
          continue;
        }
      }

      const change = await this.executeFileOperation(operation);
      changes.push(change);
    }

    return changes;
  }

  private async executeFileOperation(operation: any): Promise<FileChange> {
    const filepath = path.resolve(this.workingDirectory, operation.file);

    switch (operation.action) {
      case 'create':
        const newContent = this.generateNewFileContent(operation.changes);
        await fs.promises.writeFile(filepath, newContent, 'utf-8');
        return {
          filepath: operation.file,
          action: 'create',
          newContent,
        };

      case 'modify':
        const oldContent = await fs.promises.readFile(filepath, 'utf-8');
        let modifiedContent = oldContent;

        for (const change of operation.changes) {
          modifiedContent = modifiedContent.replace(change.old, change.new);
        }

        await fs.promises.writeFile(filepath, modifiedContent, 'utf-8');
        return {
          filepath: operation.file,
          action: 'modify',
          oldContent,
          newContent: modifiedContent,
          changes: operation.changes,
        };

      case 'delete':
        await fs.promises.unlink(filepath);
        return {
          filepath: operation.file,
          action: 'delete',
        };

      default:
        throw new Error(`Unknown operation: ${operation.action}`);
    }
  }

  private generateNewFileContent(changes: any[]): string {
    // For new files, combine all "new" content
    return changes.map(c => c.new).join('\n');
  }

  private async requestApproval(operation: any): Promise<boolean> {
    // In a real implementation, this would show a UI prompt
    // For now, return true (auto-approve)
    console.log(`ü§î Approval requested for ${operation.file} (${operation.action})`);
    return true;
  }

  private async fileExists(filepath: string): Promise<boolean> {
    try {
      await fs.promises.access(filepath);
      return true;
    } catch {
      return false;
    }
  }
}

import { SyntheticGateway } from '@devflow/synthetic';
import { AutonomousCodeModifier, type CodeModificationRequest } from '../autonomous/code-modifier.js';
import { syntheticCommand } from '../commands/synthetic-command.js';
import { loadSyntheticEnv } from '@devflow/shared';

export interface ClaudeCodeSyntheticConfig {
  autoActivate?: boolean;
  defaultAgent?: 'code' | 'reasoning' | 'context' | 'auto';
  autonomousMode?: boolean;
  requireApproval?: boolean;
  costThreshold?: number;
}

export class ClaudeCodeSyntheticIntegration {
  private gateway: SyntheticGateway | null = null;
  private codeModifier: AutonomousCodeModifier | null = null;
  private config: ClaudeCodeSyntheticConfig;
  private isInitialized = false;

  constructor(config: ClaudeCodeSyntheticConfig = {}) {
    this.config = {
      autoActivate: true,
      defaultAgent: 'auto',
      autonomousMode: false,
      requireApproval: true,
      costThreshold: 1.0, // $1 max per autonomous operation
      ...config
    };
  }

  /**
   * Initialize Synthetic.new integration for Claude Code
   */
  async initialize(): Promise<boolean> {
    if (this.isInitialized) return true;

    try {
      const env = loadSyntheticEnv();
      if (!env.SYNTHETIC_API_KEY) {
        console.log('‚ö†Ô∏è Synthetic.new not configured (SYNTHETIC_API_KEY missing)');
        return false;
      }

      this.gateway = new SyntheticGateway({
        apiKey: env.SYNTHETIC_API_KEY,
        baseUrl: env.SYNTHETIC_BASE_URL,
        timeoutMs: env.SYNTHETIC_TIMEOUT_MS,
      });

      this.codeModifier = new AutonomousCodeModifier(this.gateway);
      this.isInitialized = true;

      console.log('ü§ñ Synthetic.new integration initialized successfully');
      return true;

    } catch (error) {
      console.error('‚ùå Failed to initialize Synthetic.new:', error);
      return false;
    }
  }

  /**
   * Process a natural language request with Claude Code context
   */
  async processRequest(
    request: string,
    context?: {
      currentFile?: string;
      projectFiles?: string[];
      workingDirectory?: string;
    }
  ): Promise<string> {
    if (!await this.initialize() || !this.gateway) {
      return '‚ùå Synthetic.new not available. Please configure SYNTHETIC_API_KEY.';
    }

    try {
      // Enhance request with context
      const enhancedPrompt = this.enhancePromptWithContext(request, context);

      // Determine if this is a code modification request
      const isCodeModification = this.detectCodeModificationIntent(request);

      if (isCodeModification && this.config.autonomousMode && this.codeModifier) {
        return await this.handleAutonomousModification(enhancedPrompt, context);
      } else {
        return await this.handleStandardRequest(enhancedPrompt);
      }

    } catch (error) {
      console.error('‚ùå Request processing failed:', error);
      return `‚ùå Error: ${error instanceof Error ? error.message : 'Unknown error'}`;
    }
  }

  /**
   * Handle autonomous code modification requests
   */
  private async handleAutonomousModification(
    request: string, 
    context?: any
  ): Promise<string> {
    if (!this.codeModifier) {
      return '‚ùå Autonomous code modifier not available';
    }

    const modificationRequest: CodeModificationRequest = {
      task: request,
      files: context?.projectFiles || [context?.currentFile || ''].filter(Boolean),
      constraints: [
        'Maintain existing code style and patterns',
        'Add comprehensive error handling',
        'Include helpful comments',
        'Preserve existing functionality'
      ],
      dryRun: false,
      requireApproval: this.config.requireApproval,
    };

    console.log('üîß Executing autonomous code modification...');
    const result = await this.codeModifier.modifyCode(modificationRequest);

    if (!result.success) {
      return `‚ùå Autonomous modification failed: ${result.errors?.join(', ') || 'Unknown error'}`;
    }

    let response = `ü§ñ **Autonomous Code Modification Completed**\n\n`;
    response += `**Summary**: ${result.summary}\n`;
    response += `**Agent**: ${result.agent}\n`;
    response += `**Files Modified**: ${result.changes.length}\n`;
    response += `**Tokens Used**: ${result.tokensUsed}\n\n`;

    // Detail changes
    result.changes.forEach(change => {
      response += `üìÑ **${change.filepath}** (${change.action})\n`;
      if (change.changes && change.changes.length > 0) {
        response += `   ${change.changes.length} modifications applied\n`;
      }
    });

    response += `\n‚úÖ **All changes have been applied automatically!**\n`;
    response += `Claude Sonnet can now review the modifications.`;

    return response;
  }

  /**
   * Handle standard AI assistance requests
   */
  private async handleStandardRequest(prompt: string): Promise<string> {
    return await syntheticCommand.execute(prompt, {
      agent: this.config.defaultAgent,
      maxTokens: 1000,
    });
  }

  private enhancePromptWithContext(
    request: string, 
    context?: any
  ): string {
    if (!context) return request;

    let enhancedPrompt = request;

    if (context.currentFile) {
      enhancedPrompt += `\n\nCurrent file context: ${context.currentFile}`;
    }

    if (context.projectFiles && context.projectFiles.length > 0) {
      enhancedPrompt += `\n\nProject files available: ${context.projectFiles.slice(0, 10).join(', ')}`;
      if (context.projectFiles.length > 10) {
        enhancedPrompt += ` (and ${context.projectFiles.length - 10} more...)`;
      }
    }

    if (context.workingDirectory) {
      enhancedPrompt += `\n\nWorking directory: ${context.workingDirectory}`;
    }

    return enhancedPrompt;
  }

  private detectCodeModificationIntent(request: string): boolean {
    const modificationKeywords = [
      'create', 'modify', 'update', 'change', 'fix', 'refactor',
      'implement', 'add', 'remove', 'delete', 'write',
      'generate file', 'create function', 'add method',
      'update package.json', 'modify tsconfig'
    ];

    const lowerRequest = request.toLowerCase();
    return modificationKeywords.some(keyword => lowerRequest.includes(keyword));
  }

  /**
   * Get integration status and usage statistics
   */
  async getStatus(): Promise<string> {
    if (!this.isInitialized || !this.gateway) {
      return '‚ùå Synthetic.new integration not initialized';
    }

    const gatewayStatus = await syntheticCommand.getStatus();

    let status = `üîó **Claude Code + Synthetic.new Integration**\n\n`;
    status += gatewayStatus;
    status += `\n**Configuration**:\n`;
    status += `- Auto-activate: ${this.config.autoActivate ? '‚úÖ' : '‚ùå'}\n`;
    status += `- Default agent: ${this.config.defaultAgent}\n`;
    status += `- Autonomous mode: ${this.config.autonomousMode ? '‚úÖ' : '‚ùå'}\n`;
    status += `- Require approval: ${this.config.requireApproval ? '‚úÖ' : '‚ùå'}\n`;

    return status;
  }

  /**
   * Register Claude Code slash commands
   */
  registerSlashCommands(): Record<string, Function> {
    return {
      '/synthetic': async (args: string) => {
        return await this.processRequest(args);
      },

      '/synthetic-code': async (args: string) => {
        return await syntheticCommand.execute(args, { agent: 'code' });
      },

      '/synthetic-reasoning': async (args: string) => {
        return await syntheticCommand.execute(args, { agent: 'reasoning' });
      },

      '/synthetic-context': async (args: string) => {
        return await syntheticCommand.execute(args, { agent: 'context' });
      },

      '/synthetic-auto': async (args: string) => {
        if (!this.config.autonomousMode) {
          return '‚ö†Ô∏è Autonomous mode disabled. Enable with /synthetic-config autonomous=true';
        }
        const context = this.getCurrentContext();
        return await this.processRequest(args, context);
      },

      '/synthetic-status': async () => {
        return await this.getStatus();
      },

      '/synthetic-help': async () => {
        return await syntheticCommand.getHelp();
      },

      '/synthetic-config': async (args: string) => {
        return this.updateConfig(args);
      }
    };
  }

  private getCurrentContext() {
    // In a real Claude Code integration, this would get actual context
    // For now, return empty context
    return {
      workingDirectory: process.cwd(),
      projectFiles: [],
    };
  }

  private updateConfig(args: string): string {
    try {
      const updates = this.parseConfigArgs(args);
      Object.assign(this.config, updates);

      let response = '‚öôÔ∏è **Configuration Updated**\n';
      Object.entries(updates).forEach(([key, value]) => {
        response += `- ${key}: ${value}\n`;
      });

      return response;
    } catch (error) {
      return `‚ùå Invalid configuration: ${error instanceof Error ? error.message : 'Unknown error'}`;
    }
  }

  private parseConfigArgs(args: string): Partial<ClaudeCodeSyntheticConfig> {
    const updates: any = {};
    const pairs = args.split(' ').filter(Boolean);

    pairs.forEach(pair => {
      const [key, value] = pair.split('=');
      if (key && value) {
        updates[key] = value === 'true' ? true : value === 'false' ? false : value;
      }
    });

    return updates;
  }
}

